var ToolbarModule = Class.$extend({
    __init__: function (options) {
        this.moduleToolbarID = options.moduleToolbarID;
        this.jsModuleToolbar = jQuery("#" + this.moduleToolbarID);

        this.moduleToolbarContainerID = options.moduleToolbarContainerID;
        this.jsModuleToolbarContainer = jQuery("#" + this.moduleToolbarContainerID);
        this.editorHandler = options.editorHandler;
        this.setupToolMap();
        this.setUpTools();
        this.initListeners()
    },
    initListeners: function () {
        this.jsModuleToolbar.on("click", jQuery.proxy(this.toolbarClickHandler, this));// stop propagation except for dropdowns
    },

    getModuleToolbar: function () {
        return this.jsModuleToolbar;
    },

    getToolbarHandler: function () {
        return this.jsModuleToolbar.data("jsToolbarToolsHandler");
    },

    setupToolMap: function () {
        this.toolMap = {
            "delete-module": "DeleteModule",
            "parents-dropdown": "ParentsDropdown",
            "style": "ModuleStyle", //currently unused
            "background-color": "BackgroundColor",
            "promo-navigation": "PromoNavigation",
            "layout": "Layout",
            "district-locking": "DistrictLocking",
            "district-visibility": "DistrictVisibility",
            "module-css": "ModuleCss",
            "background-image": "BackgroundImage",
            "image-module-image": "ImageModuleImage",
            "image-module-image2": "ImageModuleImage2",
            "video-module-video": "VideoModuleVideo",
            "promo-module-video": "PromoModuleVideo",
            "border-color": "BorderColor",
            "line-style": "LineStyle",
            "line-weight": "LineWeight",
            "opacity": "Opacity",
            "padding": "Padding",
            "corner-radius": "CornerRadius",
            "promo-transition": "PromoTransition",
            "title-module": "TitleModule",
            "title-attribute": "TitleAttribute",
            "subtitle": "Subtitle",
            "file-dropdown": "FileDropdown",
            "alt-text": "AltText",
            "link-module": "LinkModule",
            "copy-module": "CopyModule",
            "make-district-module": "MakeDistrictModule",
            "dev-tools": "DevTools",
            "make-list-of-links-module": "MakeListOfLinksModule",
            "mass-add-links": "MassAddLinks",
            "link-advanced": "LinkAdvanced",
            "add-mod-above": "AddModAbove",
            "add-mod-below": "AddModBelow",
            "move-mod-up": "MoveModUp",
            "start-template": "ToggleStartTemplate",
            "css-class": "CssClass",
            "twitter-name": "TwitterName",
            "news-options": "NewsOptions",
            "rss-options": "RSSOptions",
            "locker-options": "LockerOptions",
            "staff-directory-options": "StaffDirectoryOptions",
            "calendar-options": "CalendarOptions",
            "top-level-selector": "TopLevelSelector",
            "social-media-links": "SocialMediaLinks",
            "delete-slide": "DeleteSlide",
            "crop-image": "CropImage",
            "custom-code": "CustomCode",
            "column-name": "ColumnName",
            "google-calendar-options": "GoogleCalendarOptions",
            "google-forms-options": "GoogleFormsOptions",
            "google-maps-options": "GoogleMapsOptions",
            "anchor-tag": "AnchorTag",
            "aspect-ratio": "AspectRatio",
            "background-region": "BackgroundRegion",
            "role-on-page": "RoleOnPage",
            "show-caption": "ImageCaptionCheck",
            "add-padding": "ColumnPaddingCheck",
            "strip-if-embedded": "StripIfEmbedded",
            "add-event": "AddEvent",
            "add-news": "AddNews",
            "add-note": "AddNote",
            "dropdown-width": "DropdownWidth",
            "hover_or_click_drop": "HoverOrClickDrop",
            "slideshow-ratio": "SlideshowRatio",
            "title-subtitle": "TitleSubtitle",
            "video-options": "VideoOptions",
            "title-alignment": "TitleAlignment",
            "locker-add-file": "LockerAddFile",
            "slideshow-duration": "SlideshowDuration",
            "add-assignment-plus": "AddAssignmentPlus",
            "add-assignment-standard": "AddAssignmentStandard"
        };
    },
    displayToolbar: function () {
        sl.log("displayToolbar this toolbar", this);
        var newActiveToolbar = this.jsModuleToolbar.clone(true).attr("id", null).css("display", "flex");
        var toolbarPlacement = jQuery("#toolbarPostion").val();
        this.jsModuleToolbarContainer.empty();
        switch (toolbarPlacement) {
            case "default-top":
                this.jsModuleToolbarContainer.css("postion:relative");
                break;
            case "module-top":
                var position = jQuery("#" + this.moduleToolbarID).closest(".cms2-selectable-module").offset();

                var css = {
                    position: "absolute",
                    "z-index": 12,
                    "background-color": "#fff",
                    "top": position.top - 40 + "px",
                    "left": 0,
                    "right": 0,
                    "margin-left": "auto",
                    "margin-right": "auto",
                    "align-items": "center",
                    "justify-content": "center"
                };
                this.jsModuleToolbarContainer.css(css);
                break;
        }
        this.jsModuleToolbarContainer.append(newActiveToolbar);
        for (var toolName in this.toolbarTools) {
            var tool = this.toolbarTools[toolName];
            tool.selectBlock();
        }

        if (this.jsModuleToolbarContainer.hasClass("jsContextmenu")) {
            this.jsModuleToolbarContainer.find("#jsTextToolsHolderId").attr("id", "jsContextMenuTextTools");
        }
    },

    undisplayToolbar: function () {
        //sl.log("undisplayToolbar")
        this.jsModuleToolbarContainer.children().fadeOut(jQuery.proxy(this.emptyToolbar, this));
        for (var toolName in this.toolbarTools) {
            var tool = this.toolbarTools[toolName];
            tool.deselectBlock();
        }
    },

    setUpTools: function () {
        var tools = this.jsModuleToolbar.find(".jsModuleTool");
        var i, tooltype, initTool;
        this.toolbarTools = {};
        for (i = 0; tools[i]; i++) {
            tooltype = jQuery(tools[i]).data("tool-type");
            var index = this.toolMap[tooltype];
            if (index != null) {
                initTool = window[this.toolMap[tooltype]](this);
                initTool.setUpListener();
                this.toolbarTools[tooltype] = initTool;
            } else {
                sl.log("tool type undefined:" + tooltype);
            }
        }
    },

    toolbarClickHandler: function (event) {
        var target = jQuery(event.target);
        //sl.log("toolbarClickHandler",target);
        if (!target.hasClass("jsDropButton") && target.parents(".jsDropButton").length == 0) {
            event.stopPropagation();
        }
    },
});


//Tool classes require
