var ToolbarTools = Class.$extend({
    __init__: function (toolbar) {
        //sl.log("init ToolbarTools")
        this.toolbar = toolbar;
        this.jsToolbar = toolbar.getModuleToolbar();
        this.editorHandler = toolbar.editorHandler;
        this.jsToolbar.data("jsToolbarToolsHandler", this);  //not right
    },
    setUpListener: function () {
        if (this.button != null) {
            this.button.off("click").on("click", jQuery.proxy(this.buttonClickHandler, this));
        }
        if (this.field != null) {
            this.field.on("change", jQuery.proxy(this.fieldChangeHandler, this));

        }
        if (this.checkbox != null) {
            this.checkbox.on("change", jQuery.proxy(this.checkboxChangeHandler, this));
        }
        if (this.radio != null) {
            this.radio.on("change", jQuery.proxy(this.radioChangeHandler, this));
        }

    },


    buttonClickHandler: function (event) {
        //sl.log("buttonClickHandler", event)
    },

    selectBlock: function () {
    },

    deselectBlock: function () {
        // called when block toolbar is for is deselected
    },

    // used by other classes to get the current block id
    getBlockID: function () {
        var id = this.editorHandler.getBlockID();
        return id;
    },

    ajaxCallAttributeSetting: function (options) {
        var url = options.url;
        var id = this.editorHandler.getBlockID();
        var formDataID = this.editorHandler.getFormDataID();
        var variables = {};
        variables["edit_form_data"] = formDataID;
        variables["block_id"] = id;
        var key;
        var attributes = options.attributes;
        for (key in attributes) {
            if (attributes.hasOwnProperty(key)) {
                variables[options.attributePrefix + key] = attributes[key];
            }
        }
        sl.addAjaxScope(variables);

        jQuery.ajax({
            type: "POST",
            context: this,
            url: url,
            data: variables,
            success: options.callback
        });
    },

    setAttribute: function (attributes) {
        var options = {
            url: "/pf4/cms2/setStyleAttribute",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: "style_"
        };
        this.ajaxCallAttributeSetting(options);
    },
    setImageOnlyCropAttribute: function (attributes, optionalCallback) {
        var callBack = optionalCallback || jQuery.proxy(this.setAttributeSuccess, this)
        var options = {
            url: "/pf4/cms2/setImagePositionAndSize",
            attributes: attributes,
            callback: optionalCallback,
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setAttributeSuccess: function (data) {
        vex.closeAll();
        var editor = EditCMS2Page.findModule();
        editor.updateBlock(this.editorHandler.getBlockID(), true);
    },

    setPromoAttribute: function (attributes) {
        var options = {
            url: "/pf4/cms2/setSlideshowProperties",
            attributes: attributes,
            callback: jQuery.proxy(this.setPromoAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setAttributeWithCall: function (call, attributes) {
        var options = {
            url: "/pf4/cms2/" + call,
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setBlockTitleAttribute: function (attributes) {
        var options = {
            url: "/pf4/cms2/setIframeLabel",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setBlockTitle: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockTitle",
            attributes: attributes,
            callback: jQuery.proxy(this.setPromoAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setBlockSubTitle: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockSubtitle",
            attributes: attributes,
            callback: jQuery.proxy(this.setPromoAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setBackgroundFromLocker: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockBackgroundImageFromLocker",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setAltText: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockAltText",
            attributes: attributes,
            // callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setLink: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockLink",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setPromoAttributeSuccess: function () {
        //sl.log("Promo or locking updated::", data);
        var editor = EditCMS2Page.findModule();
        editor.updateBlock(this.editorHandler.blockID, true);
    },
    setDistrictLocking: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockLocking",
            attributes: attributes,
            callback: jQuery.proxy(this.setPromoAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setDistrictVisibility: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockVisibility",
            attributes: attributes,
            callback: jQuery.proxy(this.setPromoAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },

    setBlockFormat: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockFormat",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },


    setSelectedMenuItem: function (dataType) {
        this.currentlySelected = this.button.closest("[data-selected]").data("selected")
        this.button.closest("[data-selected]").find("[data-" + dataType + "='" + this.currentlySelected + "']").addClass("sl-cms2-dropdown-menu__list-item-selected");

    },
    checkboxChangeHandler: function (event) {
        var target = jQuery(event.target);
        var navSelected = target.attr("name");
        var value = target.is(':checked');
        this.attributes = {}
        this.attributes[navSelected] = value;

    },
    radioChangeHandler: function (event) {
        var target = jQuery(event.target);
        var navSelected = target.attr("name");
        var value = target.val();
        this.attributes = {}
        this.attributes[navSelected] = value;

    },
    validateNumberUnit: function (value) {
        var n = parseFloat(value);
        var p = value.match(/%|em/);
        var output = (isNaN(n)) ? '' : (p) ? n + p : Math.round(n) + 'px';
        return output;
    },
    setCustomCssClass: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockCssClass",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setTwitterName: function (attributes) {
        var options = {
            url: "/pf4/cms2/setTwitterHandle",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setOfferCaption: function (attributes) {
        var options = {
            url: "/pf4/cms2/setImageOfferCaption",
            attributes: attributes,
            callback: jQuery.proxy(this.setImageOfferCaptionSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setAddPadding: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockAddPadding",
            attributes: attributes,
            callback: jQuery.proxy(this.setAddPaddingSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setStripIfEmbedded: function (attributes) {
        var options = {
            url: "/pf4/cms2/setBlockStripIfEmbedded",
            attributes: attributes,
            callback: jQuery.proxy(this.setStripIfEmbeddedSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setMegaMenuDropdownCSSClass: function (attributes) {
        var options = {
            url: "/pf4/cms2/setMegaMenuDropdownCSSClass",
            attributes: attributes,
            callback: jQuery.proxy(this.setAttributeSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
    },
    setHoverOrClickDrop: function (attributes) {
        var options = {
            url: "/pf4/cms2/setMegaMenuClickToOpen",
            attributes: attributes,
            callback: jQuery.proxy(this.setImageOfferCaptionSuccess, this),
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
