var ParentsDropdown = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init ParentsDropdown")
        this.$super(toolbar);
        this.editorHandler = toolbar.editorHandler;

    },
    setUpListener: function () {
        //sl.log("setup ParentsDropdown listener")
        this.jsToolbar.on({
            mouseenter: jQuery.proxy(this.parentItemEnterHandler, this),
            mouseleave: jQuery.proxy(this.parentItemLeaveHandler, this),
            click: jQuery.proxy(this.parentClickHandler, this),
        }, ".jsParentItem");

    },
    parentItemEnterHandler: function (event) {
        var parentItem = jQuery(event.target);
        var hoverOnBlockSelector = "#block_" + parentItem.data("id");
        jQuery(hoverOnBlockSelector).addClass("cms2-selectable-module--selected")
    },
    parentItemLeaveHandler: function (event) {
        var parentItem = jQuery(event.target);
        var hoverOnBlockSelector = "#block_" + parentItem.data("id");
        jQuery(hoverOnBlockSelector).removeClass("cms2-selectable-module--selected")
    },
    parentClickHandler: function (event) {
        var parentItem = jQuery(event.target);
        var editor = EditCMS2Page.findModule();
        editor.parentSelectHandler(parentItem.data("id"));
    }

})

