var MakeListOfLinksModule = ToolbarTools.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsMakeListOfLinks");
    },

    buttonClickHandler: function () {
        var editor = this.editorHandler.getCKEditor();
        var selection = editor.getSelection();
        var text = selection.getSelectedText();
        var startElement = selection.getStartElement();
        var html = this.getHTMLOfSelection();
        var cmsModule = EditCMS2Page.findModule();
        var beforeID = this.editorHandler.getBlockID();
        this.aTagList = [];
        //var parsedHTML = jQuery.parseHTML(html);
        //jQuery(parsedHTML).find("a").each(jQuery.proxy(this.processATag,this));
        jQuery("#schoolloop_scratch_pad").html(html);
        jQuery("#schoolloop_scratch_pad").find("a").each(jQuery.proxy(this.processATag, this));
        cmsModule.addBlock("cms2_link_list", beforeID, null, null, false, false, jQuery.proxy(this.insertLinks, this));
    },

    insertLinks: function (listOfLinksBlockID) {
        this.aTagList.reverse();
        this.insertNextLink(listOfLinksBlockID);
    },

    insertNextLink: function (listOfLinksBlockID) {
        if (this.aTagList.length > 0) {
            var currentATag = this.aTagList.pop();
            var url = currentATag.attr("href");
            var title = currentATag.text();
            var variables = {
                edit_form_data: this.editorHandler.getFormDataID(),
                block_id: listOfLinksBlockID,
                title: title,
                url: url
            };
            sl.addAjaxScope(variables);
            jQuery.ajax({
                url: "/pf4/cms2/addLinkToList",
                data: variables,
                context: this,
                success: jQuery.proxy(this.insertNextLink, this, listOfLinksBlockID),
                error: function (data) {
                    sl.alert(data.responseText);
                }
            });
        } else {
            var cmsModule = EditCMS2Page.findModule();
            cmsModule.updateDocument(listOfLinksBlockID);
        }
    },

    processATag: function (index, element) {
        if (jQuery(element).is("a")) {
            this.aTagList.push(jQuery(element));
        }
    },

    // copied from https://stackoverflow.com/questions/5083682/get-selected-html-in-browser-via-javascript
    getHTMLOfSelection: function () {
        var range;
        if (document.selection && document.selection.createRange) {
            range = document.selection.createRange();
            return range.htmlText;
        } else if (window.getSelection) {
            var selection = window.getSelection();
            if (selection.rangeCount > 0) {
                range = selection.getRangeAt(0);
                var clonedSelection = range.cloneContents();
                var div = document.createElement('div');
                div.appendChild(clonedSelection);
                return div.innerHTML;
            } else {
                return '';
            }
        } else {
            return '';
        }
    },

    dialogCallback: function (data) {
        if ((data != null) && (data != false)) {
            var title = data.title;
            this.editorHandler.makeDistrictBlock(title);
        }
    }
});


