var BackgroundImage = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init BackgroundImage");
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsBackgroundImage .jsLockerSelect, .jsBackgroundImage .jsRemoveBackground");
        this.uploadFileButton = this.jsToolbar.find(".jsBackgroundImage .jsUploadBackgroundImage");

        this.initBackgroundImageUploadWidget();
    },
    buttonClickHandler: function (event) {
        var target = jQuery(event.target);
        if (target.hasClass("jsLockerSelect")) {
            this.selectFromLockerHandler();
        }
        if (target.hasClass("jsRemoveBackground")) {
            this.removeImage();
        }
    },


    selectFromLockerHandler: function () {
        sl.log("selectFromLockerHandler");
        var objectName = "image" + this.editorHandler.getBlockID();
        var url = "/pf4/resource/select_resource?resource_type=image_folder"
        window[objectName] = this;
        var variables = {};
        variables.object_name = objectName;
        variables.mod = "container-table";
        sl.addAjaxScope(variables);

        var dialogData = {
            url: url,
            contentClassName: "sl-dialog-wide",
            additonalData: variables
        };
        var dialog = new Dialog(dialogData);
        dialog.createDialog();
    },
    selectResource: function (resourceID, title, url, embedURL, isGoogle, externalID) {
        var attributes = {
            resource_id: resourceID,
        };
        this.setBackgroundFromLocker(attributes);
    },


    initBackgroundImageUploadWidget: function () { // this is now only used for slidelayout
        this.editorHandler.initUploadWidget();
    },


    addHandler: function (e, data) {
        this.jqXHR = this.progressInstance.addHandler(e, data);
        this.jqXHR.error(jQuery.proxy(this.uploadErrorHandler, this));
    },


    initProgressBar: function () {
        this.jsProgressItemTemplate = jQuery("#html-helpers").find(".jsUploadItemTemplate").first();

        this.jsProgressItemHolder = jQuery("#" + this.editorHandler.getBlockID());

        this.jsProgressItemHolder.hide();
        var options = {
            itemTemplate: this.jsProgressItemTemplate,
            itemsContainer: this.jsProgressItemHolder
        };
        this.progressInstance = new ProgressBar(options);


    },
    uploadErrorHandler: function (jqXHR, textStatus, errorThrown) {
        if (errorThrown === 'abort') {
            this.jsProgressItemHolder.fadeOut();
        } else {
            sl.alert('An error occured during the image upload');
        }
    },
    removeImage: function () {
        var attributes = {
            resource_id: null
        };
        this.setBackgroundFromLocker(attributes);
    }


});


var ImageModuleImage = ToolbarTools.$extend({
    __init__: function (toolbar) {
        sl.log("init ImageModuleImage");
        this.$super(toolbar);

        var jsUploadFileButton = this.jsToolbar.find(".jsImageModuleImage .jsUploadImage").first();
        this.editorHandler.initFileUpload(jsUploadFileButton, "/pf4/cms2/setImageBlockImage", this.editorHandler.addImageHandler);
        this.button = this.jsToolbar.find(".jsImageSelectType");

    },
    buttonClickHandler: function (event) {
        var selectType = jQuery(event.target).data("select-type");
        if (selectType == "locker") {
            this.editorHandler.selectFromLockerHandler(event);
        } else if (selectType == "asset") {
            this.editorHandler.selectFromAssetsHandler(event);
        }
    }
});


