var ModuleCss = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init BackgroundImage");
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsModuleCss").first();
    },

    buttonClickHandler: function (event) {
        sl.getDeferred("cms2_toolbar").then(jQuery.proxy(this.setEditBlockToolbar, this));
        var variables = EditCMS2Page.GetVariablesToFindPage();
        var id = this.editorHandler.getBlockID();
        variables.mod = "edit_block_css";
        variables.block_id = id;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            type: "GET",
            context: this,
            url: "/pf4/cms2_sl/edit_block_css",
            data: variables,
            context: this,
            success: function (data) {
                var classes = {
                    "ui-dialog": "sl-dialog-embed-css"
                }
                jQuery("#jsModuleEditArea").html(data);
                this.dialog = jQuery("#jsModuleEditArea").dialog({
                    modal: true,
                    classes: classes
                });
            }
        });
    },

    setEditBlockToolbar: function () {
        EditBlockCss.SetToolbar(this);
    },

    deselectBlock: function () {
        EditBlockCss.Save(jQuery.proxy(this.hide, this));
    },

    hide: function () {
        jQuery("#jsModuleEditArea").hide();
    },

    hideAndUpdate: function () {
        jQuery("#jsModuleEditArea").dialog("close");
        this.update();
    },

    update: function () {
        this.editorHandler.updateBlock();
    }
});

