var LineWeight = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init LineWeight");
        this.$super(toolbar);
        this.field = this.jsToolbar.find(".jsLineWeight input");

    },
    fieldChangeHandler: function (event) {
        var target = jQuery(event.target);

        var enteredWidth = target.val();

        var validatedWidth = this.validateNumberUnit(enteredWidth);
        if (!validatedWidth) {
            sl.alert("Border width should be a CSS value, e.g. 4px");
        } else {
            target.val(validatedWidth)

            var attributes = {
                borderWidthTop: validatedWidth,
                borderWidthBottom: validatedWidth,
                borderWidthRight: validatedWidth,
                borderWidthLeft: validatedWidth
            };

            this.setAttribute(attributes);
        }

    }
});

var Opacity = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init Opacity");
        this.$super(toolbar);
        this.field = this.jsToolbar.find(".jsOpacity input");

    },
    fieldChangeHandler: function (event) {
        var target = jQuery(event.target);
        var opacity = target.val();
        if (opacity < 0 || opacity > 1 || isNaN(opacity)) {
            sl.alert("Opacity should be a value between 0 and 1.");
            target.val("");
        } else {
            this.setAttribute({opacity: opacity});
        }
    }
});

var Padding = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init Padding");
        this.$super(toolbar);
        this.field = this.jsToolbar.find(".jsPadding input");
    },
    fieldChangeHandler: function (event) {
        var target = jQuery(event.target);
        var enteredValue = target.val();
        var validatedValue = this.validateNumberUnit(enteredValue);
        if (!validatedValue) {
            sl.alert("Padding should be a CSS value, e.g. 4px");
        } else {
            target.val(validatedValue)
            var attributes = {
                paddingTop: validatedValue,
                paddingLeft: validatedValue,
                paddingBottom: validatedValue,
                paddingRight: validatedValue
            };
            this.setAttribute(attributes);
        }
    }
});


var CornerRadius = ToolbarTools.$extend({
    __init__: function (toolbar) {
        //sl.log("init CornerRadius");
        this.$super(toolbar);
        this.field = this.jsToolbar.find(".jsCornerRadius input");
    },
    fieldChangeHandler: function (event) {
        var target = jQuery(event.target);
        var enteredValue = target.val();

        var validatedValue = this.validateNumberUnit(enteredValue);
        if (!validatedValue) {
            sl.alert("Corner radius should be a CSS value, e.g. 4px");
        } else {
            target.val(validatedValue)

            var attributes = {
                borderRadiusTopLeft: validatedValue,
                borderRadiusTopRight: validatedValue,
                borderRadiusBottomLeft: validatedValue,
                borderRadiusBottomRight: validatedValue
            };
            this.setAttribute(attributes);
        }
    }
});

