var TitleModule = ToolbarTools.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
        this.field = this.jsToolbar.find(".jsTitleModule input");
        this.saveTimeoutHandler = null;
    },
    setUpListener: function () {
        this.$super();
        this.field.on('keyup paste cut ',  jQuery.proxy(this.autoSaveHandler, this));
    },
    autoSaveHandler: function (event) {
        if (this.saveTimeoutHandler == null) {
            this.saveTimeoutHandler = setTimeout(jQuery.proxy(this.asyncFieldChangeHandler, this, event),  1000);
        }
    },
    asyncFieldChangeHandler: function (event) {
        var target = jQuery(event.target);
        var title = target.val();
        var attributes = {
            title: title
        };
        var options = {
            url: "/pf4/cms2/setBlockTitle",
            attributes: attributes,
            attributePrefix: ""
        };
        this.ajaxCallAttributeSetting(options);
        this.saveTimeoutHandler = null;
    },
    fieldChangeHandler: function (event) {
        var target = jQuery(event.target);
        var title = target.val();
        var attributes = {
            title: title
        }
        this.setBlockTitle(attributes);
        this.saveTimeoutHandler = null;
    },
    updateTitleField: function (newTitle) {
        sl.log("updateTitleField with ", newTitle)
        this.toolbar["jsModuleToolbarContainer"].find(".jsTitleModule input").val(newTitle);
        this.jsToolbar.find(".jsTitleModule input").val(newTitle);
    }
});

