var LinkModule = ToolbarTools.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsLinkModule");
    },
    buttonClickHandler: function (event) {
        var dialogHtml = jQuery("#html-helpers").find("#jsModuleLinkDialog").clone();
        dialogHtml.find("[name='link']").val(this.editorHandler.getCurrentLink());
        dialogHtml.find("[name='open_in_new_window']").prop('checked', this.editorHandler.getOpenInNewWindow());
        var dialogData = {
            contentClassName: "sl-dialog-medium",
            html: dialogHtml,
            dialogCallback: jQuery.proxy(this.dialogLinkCallback, this),
        };
        var dialog = new Dialog(dialogData);
        this.currentDialog = dialog.createDialogWithHtml();
        this.initDialogListeners();
    },
    initDialogListeners: function () {
        this.urlField = this.currentDialog.find("[name=link]")
        this.urlField.on({"change": jQuery.proxy(this.urlChangeHanlder, this)})
        this.currentDialog.find(".jsTestLinkLink").attr("href", this.urlField.val())
    },
    urlChangeHanlder: function () {
        this.currentDialog.find(".jsTestLinkLink").attr("href", this.urlField.val())
    },
    dialogLinkCallback: function (data) {
        if (data != false) {
            var link = data.link;
            var open = (data.open_in_new_window) ? true : false;
            var attributes = {
                url: link,
                open_in_new_window: open
            };
            this.setLink(attributes);
        }
    }
});


