var ColumnName = ToolbarTools.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsColumnNameItem");
    },
    buttonClickHandler: function (event) {
        var target = jQuery(event.target);
        this.selectedName = target.data("col");
        this.saveName();
    },
    saveName: function () {
        var variables = {
            edit_form_data: this.editorHandler.getFormDataID(),
            block_id: this.editorHandler.getBlockID(),
            content_area_name: this.selectedName || null
        };
        sl.addAjaxScope(variables);
        jQuery.ajax({
            type: "POST",
            context: this,
            url: "/pf4/cms2_sl/setContentArea",
            data: variables,
            success: jQuery.proxy(this.saveNameSuccessHandler, this)
        });
    },
    saveNameSuccessHandler: function () {
        sl.log("saveNameSuccessHandler");
        var editor = EditCMS2Page.findModule();
        editor.updateDocument(this.selectedName);
    }
});

var AnchorTag = ToolbarTools.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
    },

    selectBlock: function () {
        this.buildMenu();
    },

    buildMenu: function () {
        var blockID = this.editorHandler.getBlockID();
        jQuery("#anchor_menu_" + blockID).empty();
        jQuery(".jsAnchorMenuTarget").each(jQuery.proxy(this.addMenuItem, this));
        jQuery("#anchor_menu_" + blockID).on("click", ".jsAnchorTagMenuItem", jQuery.proxy(this.buttonClickHandler, this));
    },

    addMenuItem: function (index, element) {
        var anchorID = jQuery(element).attr("id");
        var titleHolder = jQuery(element).find(".jsAnchorTitleHolder").first();
        if (titleHolder != null) {
            var title = jQuery(titleHolder).text();
            if ((title != null) && (title.length > 0)) {
                var blockID = this.editorHandler.getBlockID();
                var menuTemplate = jQuery("#li_" + blockID).clone();
                var aTag = jQuery(menuTemplate).find("a");
                jQuery(aTag).text(title);
                jQuery(aTag).data("anchor_id", anchorID);
                jQuery("#anchor_menu_" + blockID).append(menuTemplate);
            }
        }
    },

    buttonClickHandler: function (event) {
        var target = jQuery(event.target);
        var anchorID = target.data("anchor_id");
        this.saveAnchorID(anchorID);
    },

    saveAnchorID: function (anchorID) {
        var variables = {
            edit_form_data: this.editorHandler.getFormDataID(),
            block_id: this.editorHandler.getBlockID(),
            anchor_id: anchorID
        };
        sl.addAjaxScope(variables);
        jQuery.ajax({
            type: "POST",
            context: this,
            url: "/pf4/cms2/setAnchorID",
            data: variables,
            success: jQuery.proxy(this.saveNameSuccessHandler, this)
        });
    },
    saveNameSuccessHandler: function () {
        sl.log("saveNameSuccessHandler");
        var editor = EditCMS2Page.findModule();
        editor.updateDocument(this.selectedName);
    }
});

