var AddItemTool = ToolbarTools.$extend({
    getPublishHandler: function () {
        return jQuery.proxy(this.publishHandler, this);
    },
    buttonClickHandler: function (event) {
        var target = jQuery(event.target);
        var dataHtml = ''
        var options = {
            html: dataHtml,
            contentClassName: "sl-dialog-cms1-table-form",
            buttons: [
                jQuery.extend({}, vex.dialog.buttons.YES, {
                    className: 'vex-dialog-button-primary vex-dialog-button vex-first',
                    text: 'Publish',
                    click: jQuery.proxy(this.confirmHandler, this)
                }),
                jQuery.extend({}, vex.dialog.buttons.NO, {
                    className: 'vex-dialog-button-secondary vex-dialog-button vex-last',
                    text: 'Cancel',
                    click: jQuery.proxy(this.cancelHandler, this)
                })
            ],
            submitFunction: jQuery.proxy(this.submitFunction, this)
        };
        var dialog = new Dialog(options);
        var openCallback = jQuery.proxy(this.openCallback, this);
        this.confirmFunction = this.getPublishHandler();
        this.currentDialog = dialog.createDialogWithHtml(openCallback);
    },

    openCallback: function () {
        jQuery("#jsAddEventIframe").on({load: jQuery.proxy(this.iframeLoadHandler, this)})
    },

    iframeLoadHandler: function (event) {
        this.contents = jQuery("#jsAddEventIframe").contents();
        var path = jQuery("#jsAddEventIframe")[0].contentWindow.location.pathname.split('/')[1];
        this.form = this.contents.find("#container_content").children().not("#page_title");
        if (path == "portal" || path == "") {//close because being returned to homepage   - isn't needed now we have a DONE button
            vex.closeAll();
            this.editorHandler.updateBlock();
        } else if (this.contents.find(".success").length > 0) {
            this.cleanUpIframeContents()
            this.form.find(".btn").remove();
            this.currentDialog.find(".vex-first").hide();
            this.currentDialog.find(".vex-last").attr("class", "vex-dialog-button-primary vex-dialog-button vex-last").val("DONE");
        } else if (path == "misc") {   //for add link or upload
            this.cleanUpIframeContents()
            this.currentDialog.find(".vex-first, .vex-last").hide();//hide buttons

        } else { //Normal Form
            this.cleanUpIframeContents()
            this.form.find("a:last").add(this.form.find("a:last").prev()).hide();
            this.currentDialog.find(".vex-first, .vex-last").show();
        }
        jQuery("#jsAddEventIframe").fadeIn();
        vex.hideLoading();
    },

    submitFunction: function (event) {
        vex.showLoading();
        event.preventDefault();
    },

    cleanUpIframeContents: function () {
        this.contents.find("#container_page").attr("id", null).siblings().hide();
        this.contents.find("#container_content").attr("id", null);
        this.contents.find(".content_width").css("width", "100%")
        this.contents.find("#page_title").remove();
        this.contents.find("body, .container, .highlight_dark").css("background", "none")
    },

    confirmHandler: function () {
        this.confirmFunction()
    },

    publishHandler: function () {
        jQuery("#jsAddEventIframe").fadeOut();
        var htmlData = this.contents.find("iframe.cke_wysiwyg_frame").contents().find(".cke_editable").html(); //to refresh description field
        this.contents.find("#description").html(htmlData);
        this.form.find("#event_override").val("publishAndShare")
        this.form.find("#form").submit();
    },

    cancelHandler: function () {
        vex.closeAll();
        this.editorHandler.updateBlock();
    }
})

