var AddAssignment = AddItemTool.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsAddAssignment");
    },
    iframeLoadHandler: function (event) {
        this.contents = jQuery("#jsAddEventIframe").contents();
        var path = jQuery("#jsAddEventIframe")[0].contentWindow.location.pathname.split('/')[1];
        this.form = this.contents.find("#container_content").children().not("#page_title");
        if (this.contents.find(".success").length > 0) {
            vex.closeAll();
            this.editorHandler.updateBlock();
        } else { //Normal Form
            this.cleanUpIframeContents()
            this.form.find("a:last").add(this.form.find("a:last").prev()).hide();
            this.currentDialog.find(".vex-first, .vex-last").show();
        }
        jQuery("#jsAddEventIframe").fadeIn();
        vex.hideLoading();
    }
});

