var TitleSubtitle = ToolbarTools.$extend({
    __init__: function (toolbar) {
        this.$super(toolbar);
        this.button = this.jsToolbar.find(".jsTitleSubtitle");
    },
    buttonClickHandler: function (event) {
        var dialogHtml = jQuery("#html-helpers").find("#jsTitleSubtitleDialog").clone();

        dialogHtml.find("[name='title']").val(this.editorHandler.getTitle());
        dialogHtml.find("[name='subtitle']").val(this.editorHandler.getSubtitle());

        var dialogData = {
            contentClassName: "sl-dialog-medium",
            html: dialogHtml,
            dialogCallback: jQuery.proxy(this.dialogLinkCallback, this),
        };
        var dialog = new Dialog(dialogData);
        dialog.createDialogWithHtml();
    },
    dialogLinkCallback: function (data) {
        if (data != false) {
            this.editorHandler.saveSubtitle(data.subtitle)
                .then(this.editorHandler.saveTitle(data.title))
                .then(this.editorHandler.update())

        }
    }
});

