var TemplatePreviewModule = Class.$extend({
    __init__: function (options) {
        this.iframeHolderElement = jQuery("." + options.iframeHolderClass)
        this.iframePreviewID = options.iframePreviewID;
        this.setProportionsMap();
        this.initListeners();
    },
    setProportionsMap: function () {
        this.proportionsMap = {
            "desktop": {
                width: "1024px",
                height: "768px"
            },
            "tablet": {
                width: "768px",
                height: "1024px"
            },
            "phone": {
                width: "335px",
                height: "480px"
            }
        }
    },

    initListeners: function () {
        jQuery(document).on("click", ".jsPreviewButton", jQuery.proxy(this.clickHandler, this));
        jQuery(document).on("click", ".jsNewTabPreviewButton", jQuery.proxy(this.previewInNewTabHandler, this));
    },

    clickHandler: function (event) {
        var target = jQuery(event.target).closest(".jsPreviewButton");
        jQuery(".jsPreviewButton").removeClass("sl-button--selected");
        target.addClass("sl-button--selected")
        var previewType = target.data("preview-type");
        var newHeight = this.proportionsMap[previewType].height
        this.iframeHolderElement.css({
            width: this.proportionsMap[previewType].width,
            height: newHeight
        });
        jQuery(".cms2-preview-module--iframe--wrapper").height(newHeight);
    },

    previewInNewTabHandler: function(event) {
        event.preventDefault();
        event.stopPropagation();
        var url = jQuery("#" + this.iframePreviewID).attr("src");
        window.open(url, "sl_preview");
    }
});


