 *
 */

var DrawerHelper = Class.$extend({
    __init__: function (options) {
        sl.log("DrawerHelper options",options)
        this.drawerIframeHolder = jQuery(".jsDrawerHolder");
        this.drawerIframeWrapper = jQuery(".jsDrawerWrapper");
        this.drawerButtonClass = options.drawerButtonClass;
        this.drawerButton = jQuery("." + this.drawerButtonClass);
        this.drawerClose = jQuery(".jsDrawerClose")
        this.drawerUrl = this.drawerButton.data("drawer-url");
        this.drawerIframe = jQuery("#iframe_drawer")
        this.initListeners();
    },

    initListeners: function () {
        this.drawerButton.on("click", jQuery.proxy(this.clickDrawerHandler, this));
        this.drawerClose.on("click", jQuery.proxy(this.clickDrawerCloseHandler, this));
    },

    clickDrawerHandler: function (event) {
        sl.log("clickDrawerHandler")
        var target = jQuery(event.target);
        this.toggleDrawer()

    },
    toggleDrawer: function (button) {
        sl.log("toggleDrawer")
        var target = this.drawerButton;
        target.toggleClass("jsDrawerOpen")
        if (target.hasClass("jsDrawerOpen")) {
            var width = 450;
            var height = (jQuery("#tab_module").length == 1) ? jQuery(window).height() - this.drawerButton.offset().top + jQuery(window).scrollTop() - 150 :  jQuery(window).height() -150;
            this.drawerIframeHolder.css({
                width: width,
                "height": height,
                "overflow": "auto"
            });
                sl.log("show prefilled")
                this.drawerIframeHolder.show();
                this.drawerIframeWrapper.show("slide", {direction: "right"}, 400);
                this.drawerClose.show();
        } else {
            this.drawerIframeWrapper.hide("slide", {direction: "right"}, 400);
        }
    },

    clickDrawerCloseHandler: function (event) {
        sl.log("event", event.target);
        this.toggleDrawer()
    }
});


