var BasePlayerHandler = Class.$extend({
    __init__: function (options) {
        this.blockID = options.blockID;
        this.blockElement = jQuery("#block_" + this.blockID);
        this.listenForResize = options.listenForResize;
        if (this.listenForResize) {
            this.windowResizeTimeout = false;
            this.windowResizeDelay = 250;
            jQuery(window).resize(jQuery.proxy(this.windowResizeDebouncer, this))
        }
    },

    fixAccordionsWithVideos: function () {
        if (this.blockElement.find(".jsTitleAccordion").length == 1 && this.blockElement.find(".jsSlVid").length == 0) {
            this.blockElement.find(".jsTitleAccordionContent").hide();
        }
    },

    windowResizeDebouncer: function () {
        clearTimeout(this.windowResizeTimeout);
        this.windowResizeTimeout = setTimeout(jQuery.proxy(this.windowResizeFunctions, this), this.windowResizeDelay);
    },

    windowResizeFunctions: function () {
        //    defined in subclass if required
    },

    resizeFontForThisElement: function (element) {
        var i, elementClone;
        var origElementHeight = element.height();
        elementClone = element.clone();
        elementClone.css({
            "font-size": "",
            "max-height": "inherit"
        })
            .insertAfter(element);
        if (elementClone.height() > origElementHeight) {
            while (elementClone.height() > origElementHeight && parseInt(elementClone.css('font-size')) > 16) {
                elementClone.css('font-size', (parseInt(elementClone.css('font-size')) - 1) + "px");
            }
        }
        var newFontSize = parseInt(elementClone.css('font-size'));
        element.css({
            "font-size": newFontSize + "px",
            "line-height": (newFontSize + 2) + "px",
            "visibility": "visible"
        });
        elementClone.remove();
    }
});

