	var scrollLock = 0;
    var scrolled;
    var loc;

    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
		"ChannelBarIsSticky": false,        

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.CSVideoResize();
            this.SetTemplateProps();
            this.DynamicStyles();
            this.SchoolList();
            this.MyStart();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.HeadlinesSlider();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.PageListMenu();
            this.AppAccordion();
            this.Search();
            this.JsMediaQueries();

            loc = $("header > nav").offset().top+$("header > nav").height();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){  _this.StickyHeader(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();
            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
            $(".hp-app-slider div.ui-widget.app.headlines .ui-articles li").each(function() {
                if($(".ui-article-thumb", this).length == 0) {
                    $(this).addClass("ui-article-placeholder")
                    $(".ui-article", this).prepend('
');
                } else if($(".ui-article-thumb img", this).attr("height") != "375") {
                	$(".ui-article-thumb img", this).attr({"height": "375", "width": "425"}).css({"max-width": "425px", "max-height": "375px"});
                }
            });
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            this.CSVideoResize();

            var getWidth = $(window).width();
            if(getWidth <= 768 && $("#sw-content-container10.region.ui-hp .rs-gallery-container").length) {
                //DOC se height on mmr desc to keep content from jumping
                var scrolled = 0;
                var scrollLock = 0;
                var loc = $("#hp-slideshow-outer").outerHeight();
                $(window).scroll(function(e) {
                    scrolled = $(window).scrollTop();
                    if(scrolled >= loc && scrollLock === 0) {
                        $("#hp-slideshow .rs-gallery-desc").height($("#hp-slideshow .rs-gallery-desc").height());
                        scrollLock = 1;
                    scrollLock = 1;
                    } else if(scrolled < loc && scrollLock == 1){
                        $("#hp-slideshow .rs-gallery-desc").height("auto");
                        scrollLock = 0;
                    }
                });
            }            
        },

        "StickyHeader": function() {
        	if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var channelBarOffSet = $("nav").height();
                        if ($(window).scrollTop() <= channelBarOffSet) {
                            if(this.ChannelBarIsSticky) {
                  				$("header > nav").removeClass("fixed-element");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            if(!this.ChannelBarIsSticky) {
                                $("header > nav").hide().addClass("fixed-element");
                                $("header > nav").fadeIn(250);

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                  			$("header > nav").removeClass("fixed-element");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                        $(".hp-app-slider div.ui-widget.app").each(function() {
                                $(".more-link", this).appendTo($(".ui-widget-header", this));
                                $(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                                $(".ui-widget-header", this).removeClass("ui-helper-hidden").addClass("more-link-parent");
                        });
                        $("#hp-slideshow .rs-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-gallery-desc"));
                break;
                case "768":
                        $(".hp-app-slider div.ui-widget.app").each(function() {
                                $(".more-link", this).appendTo($(".ui-widget-header", this));
                                $(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                                $(".ui-widget-header", this).removeClass("ui-helper-hidden").addClass("more-link-parent");
                        });
                        $("#hp-slideshow .rs-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-gallery-desc"));
                break;
                case "640":
                        $(".hp-app-slider div.ui-widget.app").each(function() {
                                $(".more-link", this).appendTo($(".ui-widget-header", this));
                                $(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                                $(".ui-widget-header", this).removeClass("ui-helper-hidden").addClass("more-link-parent");
                        });
                        $("#hp-slideshow .rs-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-gallery-desc"));
                break;
                case "480":
                        $(".hp-app-slider div.ui-widget.app").each(function() {
                                $(".ui-widget-footer .clear", this).before($(".more-link", this));
                                $(".view-calendar-link", this).appendTo($(".ui-widget-footer", this));
                                $(".ui-widget-header", this).removeClass("ui-helper-hidden").addClass("more-link-parent");
                        });
                        $("#hp-slideshow .rs-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-gallery-container"));
                break;
                case "320":
                        $(".hp-app-slider div.ui-widget.app").each(function() {
                                $(".ui-widget-footer .clear", this).before($(".more-link", this));
                                $(".view-calendar-link", this).appendTo($(".ui-widget-footer", this));
                                $(".ui-widget-header", this).removeClass("ui-helper-hidden").addClass("more-link-parent");
                        });
                        $("#hp-slideshow .rs-gallery-bullets-outer").appendTo($("#hp-slideshow .rs-gallery-container"));
                break;
            }
        },

        "DynamicStyles": function() {
        	//Variables
            var headerGraphicSrc = $.trim('/cms/lib/CO02201754/Centricity/Template/GlobalAssets/images///logos/COVID-19 web header2.png');
            var srcCheck = headerGraphicSrc.indexOf("Faces/default-man.jpg") > -1;
            var footerGraphicSrc = $.trim('/cms/lib/CO02201754/Centricity/Template/GlobalAssets/images///23/gb-footer-accent.png');
            var srcCheck2 = footerGraphicSrc.indexOf("Faces/default-man.jpg") > -1;

            var styles = "";

            if((headerGraphicSrc != "") && (!srcCheck) && true) {
                $("#gb-header-left").append('');
                $("#gb-rs-menu").append('');
            } else if(true) {
                $("#gb-header-left").append('');
            	$("#gb-rs-menu").append('');
            }

            if((footerGraphicSrc != "") && (!srcCheck2) ) {
            	styles = "#gb-footer-disclaimer:before: {\n"+
                			"\tbackground: url('"+footerGraphicSrc+"') no-repeat center center;\n"+
                		 "}";
            } else {
            	styles = "#gb-footer-disclaimer:before: {\n"+
                			"\tbackground: url('/cms/lib/CO02201754/Centricity/Template/23/gb-footer-accent.png') no-repeat center center;\n"+
                		 "}";
            }

            $('head').append(
                $('', {
                    id: 'cs-dynamic-background-styles',
                    html: styles
                })
            );

        },        


       "SchoolList": function() {
            // FOR SCOPE
            var _this = this;

            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 1668,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '' + schoolList[i].schoolType + '';
                        }
                    }

                    $('#gb-schools ul').html(schools);
                }
            });
        },

        "MyStart": function() {
            // FOR SCOPE
            var _this = this;     

        	// ADD TRANSLATE
        	$(".sw-mystart-dropdown.translate .sw-dropdown").creativeTranslate({
                "type": 1,
                "languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"] - IF TRANSLATED LANGUAGE NAME IS EMPTY, THE ENGLISH NAME WILL BE USED
                    ["Spanish", "Espaol", "es"],
                    ["Arabic", "", "ar"],
                    ["Chinese Simplified", "", "zh-CN"],
                    ["Chinese Traditional", "", "zh-TW"],			
                    ["French", "franais", "fr"],
                    ["German", "Deutsche", "de"],
                    ["Italian", "italiano", "it"],
                    ["Japanese", "", "ja"],
                    ["Korean", "", "ko"],
                    ["Portuguese", "portugus", "pt"],
                    ["Russian", "", "ru"]
                ],
                "advancedOptions": {
                    "addMethod": "append",
                    "customElement": {
                        "useCustomElement": true,
                        "translateItemsList": true,
                        "customElementMarkup": "[$CreativeTranslateListItems$]"
                    }
                }
            });

            if($(".sw-mystart-button.signin").length > 0) {
                $('.sw-mystart-dropdown.users ul').append("
"+$(".sw-mystart-button.signin").html()+"");
            }

            if($(".sw-mystart-button.register").length > 0) { 
                $('.sw-mystart-dropdown.users ul').append("
"+$(".sw-mystart-button.register").html()+"");
            }

            if($('.sw-mystart-button.manage').length > 0) {
                $('.sw-mystart-button.manage a').removeAttr("id");
                $('#sw-myaccount-list li:eq(0)').clone().appendTo($('.sw-mystart-dropdown.users ul'));
                $('.sw-mystart-dropdown.users ul').append("
" + $('.sw-mystart-button.manage').html() + "");
                if($('.sw-mystart-button.pw').length > 0){
                	$('.sw-mystart-button.pw a').removeAttr("id");
                	$('.sw-mystart-dropdown.users ul').append("
" + $('.sw-mystart-button.pw').html() + "");  
                }
                $('#sw-myaccount-list li:eq(1)').clone().appendTo($('.sw-mystart-dropdown.users ul'));
            }

            $(".sw-mystart-dropdown.users a").removeAttr("tabindex");

			if($('#sw-mystart-mypasskey').length > 0) {
            	$("#ui-btn-mypasskey").html('Passkeys');
            	$('#sw-mystart-mypasskey').prependTo($('.gb-mystart-right'));
			}

            $(document).off("click", ".sw-mystart-dropdown");
            $(document).on("click", ".sw-mystart-dropdown", function(e) {
            	if($(".sw-dropdown", this).hasClass("open")) {
               		$(".sw-dropdown", this).removeClass("open").hide().attr('aria-hidden', 'true');
                } else {
                	$(".sw-dropdown", this).addClass("open").slideDown(300, "swing").attr('aria-hidden', 'false');
                }
            });

            $(".sw-mystart-dropdown").hover(function(e) {
                e.preventDefault();
            }, function() {
                $(".sw-dropdown",this).removeClass("open").hide().attr('aria-hidden', 'true');
            });

            $(document).off("focus", ".sw-mystart-dropdown");
            $(document).on("focus", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
            });

            $(document).off("keydown", ".sw-mystart-dropdown");
            $(document).on("keydown", ".sw-mystart-dropdown", function(e) {
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                    	if($(this).hasClass("translate")) {
                        	e.preventDefault();
                            $(".sw-dropdown", this).hide().attr('aria-hidden', 'true');
                            $(".sw-mystart-item.sitemap a").focus();
                        } else if($(this).hasClass("users")) {
                            $(".sw-dropdown", this).hide().attr('aria-hidden', 'true');
                        }
                    break;
                }
            });

            $(document).off("keydown", ".sw-mystart-dropdown ul a");
            $(document).on("keydown", ".sw-mystart-dropdown ul a", function(e) {
           		var lastIndex = $(this).parents(".sw-dropdown ul").children("li").length - 1;

                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(this).parent().index() == 0) {
                        	$(this).parents(".sw-mystart-dropdown").focus();
						} else {
                        	$(this).parent().prev().find("a").focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(this).parent().index() == lastIndex) {
                        	$(this).parents(".sw-mystart-dropdown").focus();
						} else {
                        	$(this).parent().next().find("a").focus();
                        }
                    break;
                    case _this.KeyCodes.space:
                    case _this.KeyCodes.enter:
                        $(this)[0].click();
                    break;  
                    case _this.KeyCodes.tab:
                        if($(this).parent().index() == lastIndex && e.keyCode == _this.KeyCodes.tab) {
                            $(".sw-dropdown").slideUp(300, "swing").attr('aria-hidden', 'true');
                            $(".sw-dropdown").removeClass("open");
                        }
                    break;                
                }
            });
        },

        "Header": function() {
            // FOR SCOPE
            var _this = this;

            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/CO02201754/Centricity/Template/GlobalAssets/images///logos/gb-logo.png");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").prepend('');
                } else {
                    $("#gb-logo").prepend('');
                }
            } else {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").prepend('');
                } else {
                    $("#gb-logo").prepend('');
                }

                // ADD SITENAME
                var siteNameOne = $.trim("Elizabeth");
                var siteNameTwo = $.trim("School District");
                if((siteNameOne == "") && (siteNameTwo == "")) {
                    var splitLen = 2;
                    var siteName = "Elizabeth School District";
                    siteName = siteName.split(" ");
                    var siteNameLength = siteName.length;
                    if(siteNameLength > 2){
                        siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                    } else {
                        siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                    }
                    siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                    $("#gb-sitename").append("
" + siteNameBegin + "
" + siteNameEnd + "");
                } else if((siteNameOne != "") && (siteNameTwo == "")) {
                    $("#gb-sitename").append("
" + siteNameOne + "");
                } else if((siteNameOne == "") && (siteNameTwo != "")) {
                    $("#gb-sitename").append("
" + siteNameTwo + "");
                } else if((siteNameOne != "") && (siteNameTwo != "")) {
                    $("#gb-sitename").append("
" + siteNameOne + "
" + siteNameTwo + "");
                }
            }

            $(document).on("click keydown", ".gb-schools-toggle", function(e) {
                if(_this.A11yClick(e)) {
                	e.preventDefault();  
                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-expanded": "true",
                            "aria-label": "Close Schools List"
                        });
                        $(this).addClass("open");
                        $("#gb-schools-list").attr("aria-hidden", "false").slideDown(250);
                    } else {
                    	$(this).attr({
                        	"aria-expanded": "false",
                            "aria-label": "Open Schools List"
                        });
                         $(this).removeClass("open");
                        $("#gb-schools-list").attr("aria-hidden", "true").slideUp(250);
                    }
                }
            });

            $(".gb-mystart-social-media > ul a").removeAttr("tabindex");
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });
        },

