                    $(".ui-article-description", this).slideUp(250);
                });

                $(".hp-app-slider div.ui-widget.app.headlines li").on('focusin', function() {
                    $(this).addClass("focused");
                    $(".ui-article-title", this).focus();
                    $(".ui-article-description", this).slideDown(250);
                });

                $(".hp-app-slider div.ui-widget.app.headlines li").on('focusout', function() {
                    var $elem = $(this);
                    setTimeout(function() {
                        var hasFocus = !! ($elem.find(':focus').length > 0);
                        if (!hasFocus) {
                            $elem.removeClass("focused");
                            $(".ui-article-description", this).slideUp(250);
                        }
                    }, 10);
                });
            }      
