        "UpcomingEvents": function() {
            // FOR SCOPE
            var _this = this;   

            $(document).on("click keydown", ".ui-show-more-btn", function(e) {
                if(_this.A11yClick(e)) {
                	e.preventDefault();
                    if($(this).parent().siblings(".ui-articles").children("li.open").length > 0) {
                    	if(_this.GetBreakPoint() == "desktop") {
                    		$(this).parent().siblings(".ui-articles").children("li:nth-child(n+6)").removeClass("open").slideUp(250);
                            $(this).html("Show More");
                    	} else {
                    		$(this).parent().siblings(".ui-articles").children("li:nth-child(n+4)").removeClass("open").slideUp(250);  
                            $(this).html("Show More");
                        }
                    } else {
                   		if(_this.GetBreakPoint() == "desktop") {
                    		$(this).parent().siblings(".ui-articles").children("li:nth-child(n+6)").addClass("open").slideDown(250);
                            $(this).parent().siblings(".ui-articles").find("a").first().focus();
                            $(this).html("Show Less");
                    	} else {
                    		$(this).parent().siblings(".ui-articles").children("li:nth-child(n+4)").addClass("open").slideDown(250); 
                            $(this).parent().siblings(".ui-articles").find("a").first().focus();
                            $(this).html("Show Less");
                        }
                    }
                }
            });

            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
                eventsByDay(".upcomingevents .ui-articles");
            }

            $(".hp-app-slider div.ui-widget.app.upcomingevents").each(function() {            
                if($(".ui-articles li", this).length > 5) {
                	$(".ui-widget-detail", this).append('
Show More');
                } else if($(".ui-articles li", this).length > 3) {
                    $(".ui-widget-detail", this).append('
Show More');
                } else if($(".ui-articles li", this).length < 3) {
                    $(".ui-articles", this).addClass("cs-less-items");
                }
            });

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                    $(".joel-day", this).prependTo($(".upcoming-column.right", this));
                    $(".sw-calendar-block-date", this).wrapInner('');
                    $(".joel-month", this).prependTo($(".sw-calendar-block-date", this));
                });
                $(".sw-calendar-block-time").each(function() {
                	$(this).html($(this).text().replace(/ AM/g, "AM"));
                    $(this).html($(this).text().replace(/ PM/g, "PM"));
                });
            }
        },

