        "AppAccordion": function() {            
            //DOC dont add app into accordion if it doesnt have a header
            $("#hp-content div.ui-widget.app").each(function() {
                if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
                    $(this).addClass("no-header");
                } else {
                    if( $('.ui-widget-header h1 .accordion-handle', this).length <= 0){
                        $('.ui-widget-header h1', this).append('');
                    }
                }
            });

            //DOC accordion header click
            $("#hp-content div.ui-widget-header").off('click').click(function(){
            	if($(this).parent().hasClass("active")) {
            		$("#hp-content div.ui-widget.app").removeClass("active");
				} else {
                    var clickedApp = $(this).parent().parent().attr("id");

                    $("#hp-content div.ui-widget.app").removeClass("active");
                    $(this).parent().addClass("active");

                    if($(this).parent().hasClass("headlines") && $(this).parents(".hp-app-slider").length) {
                      $(this).parent().appScroller({
                              "appParent"             : ".hp-app-slider",
                              "targetApp"             : "headlines", //upcomingevents or headlines
                              "imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
                              "imageNum768"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 768
                              "imageNum640"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 640
                              "imageNum480"           : 2, //NUMBER OF IMAGES TO DISPLAY AT 480
                              "imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
                              "backIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                              "nextIconFontLetter"    : "", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
                              "buttonColor"           : "#000", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
                              "extraBackIconClass"    : "cs-icon-font cs-icon-arrow",
                              "extraNextIconClass"    : "cs-icon-font cs-icon-arrow",
                              "controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
                      });
                    }

                    smoothScroll("#" + clickedApp, 10);
                }
            });	

            function smoothScroll( to, offset ){
                if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
                var target = $(to);

                if (target.length) {
                    $('html,body').animate({
                        scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
                    }, 800, function(){
                        $("html, body").stop(true, false);
                    });
                    return false;
                }
            }
        },

        "Search": function() {
            // FOR SCOPE
            var _this = this;

            // OPEN SEARCH DIALOG
            $(document).on("click keydown", "#gb-channel-search-toggle", function(e) {
                if(_this.A11yClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-expanded": "true",
                            "aria-label": "Close Search Dialog"
                        });
                        $("#gb-header-right").addClass("open");
                        $("#gb-search").attr("aria-hidden", "false").show("slide", { direction: "right" }, 250);

                        if(e.type == "keydown") {
                            setTimeout(function() {
                                $("#gb-search-input").focus();
                            }, 260);
                        }
                    } else {
                    	$(this).attr({
                        	"aria-expanded": "false",
                            "aria-label": "Open Search Dialog"
                        });
                        $("#gb-header-right").removeClass("open");
                        $("#gb-search").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 250);
                    }
                }
            });

            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "search") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("search");
                }
            });

            // FORM SUMBIT
            $("#gb-search-form").submit(function(e) {
            	e.preventDefault();
                window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
            });
        },

        "CalendarListView": function() {
             // FOR SCOPE
            var _this = this;

            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(_this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "A11yClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "CSVideoResize": function() {
            // FOR SCOPE
            var _this = this;

            if($(".hp").length) {
                switch(_this.GetBreakPoint()) {
                    case "desktop":
                        var topOffSet = $("#hp-video").offset().top;
                        var screenWidth = $(window).width();
                        var screenHeight = $(window).height();
                        var screenRatio = screenHeight / screenWidth;
                        var containerHeight = screenWidth * screenRatio-topOffSet;
                        var vidWidth = screenWidth, vidHeight = 0;
                        // GET IMAGE RATIO
                        var initW = 1500, initH = 915;
                        var ratio = initH / initW;
                        vidHeight = vidWidth * ratio;

                        if(vidHeight < screenHeight){
                            vidHeight = screenHeight;
                            vidWidth = vidHeight / ratio;
                        }
                        var centerWidth = (vidWidth - screenWidth) / 2;
                        var centerHeight = (vidHeight - screenHeight) / 2;

                        $("#hp-video").css({"height": containerHeight + "px"});
                        $("#hp-video-player").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
                    break;
                    case "768": case "640": case "480": case "320":

                    break;
                }
			}
        }
    };

    var windowWidth = $(window).width();
    var windowHeight = $(window).height();
    var container = "hp-video-player";
    var videoId = "E7ALl2LPm5Q";
    var csYoutubePlayer;
    var backgroundVideoPlayer = {
        playVideo: function(container, videoId) {
        	if("false" == "false") {
                if (typeof(YT) == 'undefined' || typeof(YT.Player) == 'undefined') {
                    window.onYouTubeIframeAPIReady = function() {
                        backgroundVideoPlayer.loadPlayer(container, videoId);
                    };
                    $.getScript('//www.youtube.com/iframe_api');
                } else {
                    backgroundVideoPlayer.loadPlayer(container, videoId);
                }
            }
        },

        loadPlayer: function(container, videoId) {
            csYoutubePlayer = new YT.Player(container, {
                videoId: videoId,
                width: windowWidth,
                height: windowHeight,
                playerVars: {
                    'autoplay': 1,
                    'modestbranding': 1,
                    'controls': 0, 
                    'rel' : 0,
                    'loop': 1,
                    'showinfo': 0,
                    'wmode': 'opaque'
                },
                events: {
                    'onReady': backgroundVideoPlayer.onPlayerReady,
                    'onStateChange': backgroundVideoPlayer.onPlayerStateChange
                }
            });
        }, 

        onPlayerReady: function(event) {
             csYoutubePlayer.mute();

             $("#hp-video-player").addClass("hide768");
             $(document).on("click keydown", "#hp-video-button", function(e) {
                if(e.type == "click" || e.type == "keydown" && (e.keyCode == 32 || e.keyCode == 13)) {
                    e.preventDefault();
                    if($(this).hasClass("pause-video")) {
                        $(this).removeClass("pause-video");
                        $(".cs-video-button-icon", this).text(">");
                        $(".cs-video-button-text", this).text("Play Video");
                        csYoutubePlayer.pauseVideo();
                    } else {
                        $(this).addClass("pause-video");
                        $(".cs-video-button-icon", this).text("II");
                        $(".cs-video-button-text", this).text("Pause Video");
                        csYoutubePlayer.playVideo();
                    } 
                }
            });

            //DOC tap to show desc on mobile
            if($(window).width() <= 480 && $(".hp").length) {
                $("#hp-video").append('
');
                $("#hp-video .rs-photo-gallery-tap-button").on("click", function() {
                    $(this).hide();
                    $("#hp-video #hp-video-content").addClass("tapped").show();
                });
                $("#hp-video #hp-video-content").on("click", function() {
                    if($(this).hasClass("tapped")) {
                        $(this).removeClass("tapped").hide();
                        $("#hp-video .rs-photo-gallery-tap-button").show();
                    } else {
                        $(this).addClass("tapped").show();
                        $("#hp-video .rs-photo-gallery-tap-button").hide();
                    }
                });
            } else {
                $("#hp-video-content").show();
                $("#hp-video-content .rs-gallery-title, #hp-video-content .rs-gallery-caption").slideDown(1000);
                $("#hp-video-content .rs-gallery-desc-more-link").addClass("videoFadeIn");
                $("#hp-video-content .rs-gallery-accent").addClass("videoFadeInSmall");
            }
        },

        onPlayerStateChange: function(event) {
            if(event.data == 0) {
                csYoutubePlayer.playVideo();
            }
        }
    };

	backgroundVideoPlayer.playVideo(container, videoId);

