$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=Op8VXEGg3%2fe8C%2bUUwQtXAw%3d%3d&SessionID=c4fcce40-824c-4b58-b899-e1a27e0951de&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+6%3a20%3a03+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.29.11';
