			url: 'https://webapps.bayschools.net/resources/liveEvents',
			async: false,
			jsonpCallback: 'schoolEventData', 
			contentType: "application/json",
			dataType: 'jsonp',
			success: function(json) {
			   console.dir(json);
				var content = "
";

                $.each(json, function(index, item) {
					var d = new Date(item.eventDate), locale = "en-us",month = d.toLocaleString(locale, { month: "short" });
					var curr_date = d.getDate();
                    content += "<div class='row calendar-item' style='margin-bottom:0;''>

" + curr_date + "
" + month + "

" + item.eventTitle + "<p style=font-size:14px;color:#888888;'>" + item.eventStart + "
";
                });
				content+=""
				$("#live-events").append(content);
			},
			error: function(e) {
			   console.log(e.message);
			}
		});	

	});
