link.script:
	//loads the IFrame Player API code asynchronously.
	var tag = document.createElement('script');

	tag.src = "https://www.youtube.com/iframe_api";
	var firstScriptTag = document.getElementsByTagName('script')[0];
	firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

	//creates an  (and YouTube player)
	//after the API code downloads.
	var player;
	var windowWidth;
	var windowHeight;
	var alertHeight;

	function onYouTubeIframeAPIReady() {
		if('MMR' == 'Video') {
        	$("#hp-slideshow").addClass("mediaType-video");
        	$('
').prependTo("#hp-slideshow");
            $("#hp-slideshow #sw-content-container10").remove();
				windowWidth = $(window).width();
				windowHeight = $(window).height();
				player = new YT.Player('player', {
					height: windowHeight,
					width: windowWidth,
					videoId: 'mj2YFL3PS2M',
					playerVars: { 
						'modestbranding': 1,
					        'controls': 0, 
					        'autoplay': 1,
					        'rel' : 0,
					        'loop': 1,
					        'wmode': 'opaque',
					        'showinfo': 0
					},
					events: {
						'onReady': onPlayerReady,
						'onStateChange': onPlayerStateChange
					}
				});
            $("#hp-video-player iframe").attr("tabindex","-1");

        	$("

Pause Video



Our Mission


Educate and develop all students today for their success tomorrow. 
Learn More").appendTo("#hp-slideshow");    

        }
	}
	//the API will call this function when the video player is ready.
	function onPlayerReady(event) {
    	if($(window).width() > 1023) {
        	$(".cs-video-btn").attr("aria-label","Pause Video");
            $(".cs-video-btn").addClass("pause");
        }
		csVideoResize();
		$(window).bind("resize", function(){ 
			csVideoResize(); 
		})
        player.mute();
        $(".cs-video-btn").click(function() {
            $(this).toggleClass("pause");
            $(this).toggleClass("play");
            pausePlayVideo();
        });
	}

	//The API calls this function when the players state changes.
	//The function indicates that when playing a video (state=1),
	//the player should play for six seconds and then stop.
	var done = false;
	function onPlayerStateChange(event) {
    	if($(window).width() > 1023) {
        	pausePlayVideo();
        }
	}
    function pausePlayVideo() {
    	if($(".cs-video-btn").hasClass("pause")) {
            $(".cs-video-btn").attr("aria-label","Pause Video");
            $(".cs-video-btn").text("Pause Video");
            player.playVideo();
        } else {
            $(".cs-video-btn").attr("aria-label","Play Video");
            $(".cs-video-btn").text("Play Video");
            player.pauseVideo();
        }
	}
	function stopVideo() {
		player.pauseVideo();
	}
    function csVideoResize() {
		 var getWidth = $(window).width();
			if (getWidth >= 1024) { // 960+
				var topOffSet = $("#hp-slideshow").offset().top;
				var screenWidth = $(window).width();
				var screenHeight = $(window).height();
				var screenRatio = screenHeight / screenWidth;
			    var containerHeight = screenWidth * screenRatio;
				var vidWidth = screenWidth, vidHeight = 0;

				// GET IMAGE RATIO
				var initW = 853.5, initH = 480;
				var ratio = initH / initW;
				vidHeight = vidWidth * ratio;
				if(vidHeight < screenHeight){
					vidHeight = screenHeight;
					vidWidth = vidHeight / ratio;
				}
		                var centerWidth = (vidWidth - screenWidth) / 2;
		                var centerHeight = (vidHeight - screenHeight) / 2;

				// APPLY THE DIMENSIONS
				$("#hp-slideshow").css({"height": containerHeight + "px"});
				$("#player").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
			} else {
            	var screenWidth = $(window).width();
                var screenHeight = $(window).height();
                var vidWidth = screenWidth, vidHeight = screenHeight;

                // GET IMAGE RATIO
                var initW = 853.5, initH = 480;
                var ratio = initH / initW;
                vidHeight = vidWidth * ratio;
                var newvidHeight = vidHeight * ratio;
                var vidPlayerHeight = screenHeight;

                $("#player").attr("tabindex","-1").css({"height": vidHeight + "px" , "width": vidWidth + "px"});
            	$("#hp-slideshow").css({"height": vidHeight + "px" , "width": screenWidth + "px"});
            }
	}

	var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
    var mediaType = 'MMR';

	$(window).load(function() {
		searchText();

        //REMOVE TOGGLED OFF BUTTONS
        $(".element-btn.false, .social-icon.false").remove();

        //ACCESSIBLE NAV APP
        $(".ui-widget.app.navigation").csAccessibleNavigationApp();

		//HIDE EMPTY DROPDOWN THAT GETS PLACED BY THE SYSTEM IF CHANNEL HAS NO SECTIONS BUT CHANNEL HOMEPAGE IS TURNED ON
		$("ul.sw-channel-dropdown").each(function() {
			var systemDropdown = $(this);
			if(!$("li",systemDropdown).length) {
				$(systemDropdown).remove();
			}
		});

        //DOC ADD HAS-DROPDOWN CLASS TO CHANNELS THAT HAVE A SECTION DROPDOWN
        $("li.sw-channel-item").each(function() {
			if($(".sw-channel-dropdown", this).length) {
				$(this).addClass("has-dropdown");
			}
		});

        //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.stop(true,true).slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

        if(!$(".lt-1024").length) {
            //if($(window).width() > 1023) {
                gbIconsPosition();
            //}
        }
        $(window).bind("resize", function(){ 
            //if($(window).width() > 1023) {
            if(!$(".lt-1024").length) {
                gbIconsPosition();
            }
            //}
        });

        canvasGradient();
        jsMediaQueries();

        $(window).bind("resize", function(){ 
			jsMediaQueries();
		});

        sameHeights();

        MyStartDropdownActions({
            "dropdownParent": ".sw-mystart-dropdown",
            "dropdownSelector": ".selector",
            "dropdown": ".sw-dropdown",
            "dropdownList": ".sw-dropdown-list"
        });

        $("#hp-content-outer").show();

        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + ($("#gb-channel-list-outer").outerHeight(true) + $("#gb-header-outer").outerHeight(true)) + "px"
                        });
	});

    $(document).scroll(function() {
        var scrollTop = $(this).scrollTop();

        if(!$(".lt-1024").length){
            if(scrollTop >= 120){
                $("#gb-page").addClass("logo-gone");
                $("#gb-mystart .sw-mystart-dropdown.translate .selector").attr("tabindex","0");
            } else {
                $("#gb-page").removeClass("logo-gone");
                $("#gb-mystart .sw-mystart-dropdown.translate .selector").attr("tabindex","-1");
            }
        }
    });

	$(function(){

		csRsMenu();
		useChannelForSection();
		dynamicContent();
        globalIcons();
        if(mediaType == "MMR") {
        	mediaRotator();	
        }

        appAccordion();
        if($("#dashboard-sidebar").length) {
        	$("#gb-page").addClass("sidebar-active");
        }
        //canvasGradient();
        $.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 3927,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */

                var schools = '';

                var schoolGroups = {};
                for( var i in schoolList ){
                    if(schoolGroups[schoolList[i].schoolType] == undefined){
                        schoolGroups[schoolList[i].schoolType] = [];
                    }

                    if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        schoolGroups[schoolList[i].schoolType].push('
' + schoolList[i].schoolName + '');
                    }
                }

                for (var i in schoolGroups ){
                    schools += '

' + i + '';
                    for (var p = 0; p < schoolGroups[i].length; p++){
                        schools += schoolGroups[i][p];
                    }
                }

                $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                $('.sw-mystart-dropdown.schoollist ul li').clone().html('.sw-mystart-dropdown.schoollist .sw-dropdown-list');
            }
        });
        $(".sw-mystart-dropdown").unbind().removeAttr("tabindex aria-expanded aria-label role");
        $(".sw-mystart-dropdown a").unbind();
        $(".sw-mystart-dropdown .sw-dropdown").attr("aria-hidden", "true");
        $(".sw-mystart-dropdown .selector").removeAttr("aria-hidden").attr({
        	"role": "button",
            "aria-expanded": "false",
            "aria-haspopup": "true",
            "tabindex": "0"
        });
        $("#gb-mystart .sw-mystart-dropdown.translate .selector").attr({
        	"tabindex": "-1"
        });
	});

    function sameHeights() {
    	if($(".hp .hp-content.two").length) {

            $("body").csRsSameHeights({
                resizeElements : [
                    {
                        "resizeMethod"		: "apps",	// OPTIONS - columns, apps
                        "parent"			: ".column-outer",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
                        "columns"			: ".region.ui-hp",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
                        "gutter"			: 40,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
                        "applySameHeights"	: ["960"]	// OPTIONS - ["960", "768", "640", "480", "320"]
                    }
                ]
            });
        }
    }

    function gbIconsPosition() {
    	var channelHeight = $("#gb-channel-list-outer").height();
        var headerHeight = $("#gb-header-outer").height();
        var topPos = channelHeight + headerHeight;

        if($(".hp").length) {
        	$(".gb-logo").css({
            	"top": channelHeight + 16
            });
        	$("#gb-header-outer").css({
            	"top": channelHeight
            });
            $("#gb-global-icons").css({
                "top": topPos
            });
            $("#header-social-icons").css({
                "top": topPos + 96
            });
        }
        if($(".sp").length || $(".spn").length) {
        	$(".gb-logo").css({
            	"top": channelHeight + 16
            });
        	$("#gb-header-outer").css({
            	"top": channelHeight
            });
        	$("#sp-content-outer, #spn-content-outer").css({
            	"margin-top": topPos	
            });
        }
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				//canvasGradient();
			break;
			case "768":
				canvasGradientMobile();
			break;
			case "640":
				canvasGradientMobile();
			break;
			case "480":
				canvasGradientMobile();
			break;
			case "320":
				canvasGradientMobile();
			break;
		}
	}

