            "accordionBreakpoints" : [480, 320]
        });
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function dynamicContent(){
		// ********** MYSTART ********** //
        if($("#sw-mystart-mypasskey").length) {
        	$("#sw-mystart-mypasskey a span").addClass("acc-hidden");
        	$(".scroll-up-btn").after($("#sw-mystart-mypasskey"));
        }

        // ADJUST PASSKEY OVERLAY
        $(document).ajaxComplete(function() {
            if($("#ui-mypasskey-overlay").length && !$("#sw-mystart-mypasskey #ui-mypasskey-overlay").length) {
                $("#sw-mystart-mypasskey").append($("#ui-mypasskey-overlay"));
            }
        });

        //CHANGE SEARCH BUTTON TEXT
        $("#sw-search-button > span").addClass("manatee-icon-font").html("");
        //MOVE SEARCH
        $("#sw-mystart-search").prependTo("#gb-search");
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();
        $("#sw-search-input").before('Search Input');
        $("#sw-search-button").attr({
        	"role": "button",
            "aria-label": "Search Our Site"
        });
        $("#sw-search-button").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
                SWGoToSearchResultsPageswsearchinput();
            }
        });

		//DOC change schoollist selector text
		$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

		//ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
		//$(".sw-mystart-dropdown").addClass("transition");

		//ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
		$(".sw-mystart-button").addClass("transition");


		// ********** HEADER ********** //
		// ADD LOGO
		var logoSrc = jQuery.trim("/cms/lib/FL02202357/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
		var srcSplit = logoSrc.split("/");
		var srcSplitLen = srcSplit.length;
		if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
			$(".gb-logo").append('').removeClass("hidden");
		} else {
			$(".gb-logo").append('').removeClass("hidden");
		}

        //SCROLL DOWN BTN
        var stickyHeaderHeight = $("#gb-fixed-header").height();
        $(".scroll-down-btn").click(function(e) {
        	e.preventDefault();
			$("html, body").animate({
				scrollTop: $("#hp-content-outer").offset().top - stickyHeaderHeight
			}, 800, function(){
				$("html, body").stop(true, false);
			});
		})

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");

		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

        $("li.sw-channel-item").each(function() {
        	var thisLi = this;
            if($(thisLi).hasClass("first") && $(thisLi).find("> a span").text() == "Home") {
            	$(thisLi).addClass("manatee-icon-font").find("> a span").html("");
            }
        });

        //DOC add aria to channels and dropdown items
		$(".sw-channel-item").attr("aria-haspopup","true");
		$(".sw-channel-dropdown li").attr("aria-haspopup","false");

		//ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
		$("ul.sw-channel-list li.sw-channel-item").addClass("transition");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");

		//DOC replace more-link text
		$(".ui-widget.app.headlines a.more-link span").html("VIEW ALL Headlines");
        $(".ui-widget.app.announcements a.more-link span").html("VIEW ALL Announcements");

		//DOC replace ui-return text for headlines
		$(".headlines.detail .ui-return span").text(" Return to Headlines");

		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();

		//DOC center signed out message and signin button
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        //DOC add sign in breadcrumb
        if($("#swlogin").length){
            $(".ui-breadcrumbs").append("
Sign In");
        }

        //REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

        //MOD EVENTS
        $(".upcomingevents").modEvents({
			columns:"yes",
			monthLong:"yes",
			dayWeek:"yes"
		});
		eventsByDay(".upcomingevents .ui-articles");
		if($("div.ui-widget.app.upcomingevents .ui-article-description").text() == "There are no upcoming events to display.") {
			$("div.ui-widget.app.upcomingevents .ui-article-description").css("padding","0px");
		}
        $(".hp-content.one .ui-widget.app.upcomingevents").each(function() {
        	$(this).find(".view-calendar-link").wrap("
");
        	$(this).find(".cs-calendar-link-cont").appendTo($(this).find(".ui-articles"));
        });
        $(".view-calendar-link span").text("View Full Calendar");

        $(".hp-content.one .ui-widget.app.upcomingevents .ui-article").each(function() {
            if($('.upcoming-column.right', this).length){
                var month = $(".upcoming-column.left .joel-month",this).text();
                var day = $(".upcoming-column.left .sw-calendar-block-date",this).text();
                var eventNums = $(".ui-article-description",this).length;
                if(eventNums >= 2){
                    var moreLink = 'View More+';
                    $('.upcoming-column.right', this).append(moreLink);
                    $(".ui-article-description:nth-child(n + 2)",this).hide();

                }
            }
            $(".cs-ue-more-link",this).on("click", function(e) {
                if($(this).hasClass("open")){
                    e.preventDefault();
                    $(this).removeClass("open").attr('aria-label','show more events for ' + month + ' ' + day);
                    $(this).parent().find(".ui-article-description:nth-child(n + 2)").slideUp();
                    $(".cs-ue-more-link-text",this).text("View More");
                    $(".view-more-icon",this).text("+");
                } else {
                    e.preventDefault();
                    $(this).addClass("open").attr('aria-label','show less events for ' + month + ' ' + day);
                    $(this).parent().find(".ui-article-description:nth-child(n + 2)").slideDown();
                    $(".cs-ue-more-link-text",this).text("View Less");
                    $(".view-more-icon",this).text("-");
                }
            });
        });


        //APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
		$("div.ui-widget.app .ui-widget-detail img")
        .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        .not($("div.ui-widget.app.headlines .ui-widget-detail img"))
        .each(function() {
          if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined || $(this).parent('p').attr('style') == 'text-align: center;' ) { // IMAGE HAS INLINE DIMENSIONS
              $(this).css({"width": $(this).attr("width") + "px", "max-width": "100%", "height": "auto", "max-height": $(this).attr("height") + "px"});
          }
        });

        //DOC MOVE ARTICLES MORE LINK
        $(".hp-content.one .ui-widget.app.headlines").each(function() {
            $(this).find(".more-link").parent().prependTo($(this).find(".ui-widget-footer"));
            $(this).find(".more-link").prependTo($(this).find(".ui-widget-footer"));
            $(this).find(".ui-widget-footer li").remove();
        });

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackText = $("div#sw-footer-links li:eq(0) a").text();
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var termsText = $("div#sw-footer-links li:eq(2) a").text();
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");
		var policyText = $("div#sw-footer-links li:eq(1) a").text();
		var copyrightText = $("#sw-footer-outer #sw-footer-copyright").text();
		var bbLogo = $("#sw-footer-logo a > img").attr("src");

		$(".cs-sw-footer-logo").html($("#sw-footer-logo").html());
		$("#cs-sw-footer-links").html("" + feedbackText + "" + termsText + "" + policyText + "" + copyrightText + "");

        // SCROLL TO TOP BUTTON
        $(".scroll-up-btn").click(function(e) {
        	e.preventDefault();
            $("html, body").animate({ scrollTop: 0 }, "slow", function() {

            });

        });
        $(".scroll-up-btn").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                    $("li.sw-channel-item:first-child > a").focus();
                });
            }
        });

        $(".resources-links a").each(function() {
        	var thisLink = this
        	if($(thisLink).text() == "") {
            	$(thisLink).parent().remove();
            }
        });

        if(!$(".resources-links").length) {
        	$(".gb.footer.two").remove();
        }
	}

	function eventsByDay(container) {
		var countLi = 0;
        var csMonth = "";
		$(".ui-article", container).each(function(){
        	if (!$(".sw-calendar-block-date", this).length){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			}

            $(this).find(".upcoming-column.left .joel-month").appendTo($(this).find(".upcoming-column.left"));
			$(this).find(".upcoming-column.left .joel-day").appendTo($(this).find(".upcoming-column.left"));

            //Add 'ALL DAY' Text to full day events
            $('.upcoming-column.right .ui-article-description', this).each(function(){ 
                if($('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                    $(this).prepend('ALL DAY');
                }
            });



		});

        $(".upcomingevents .ui-article").each(function() {
        	// ADD ZERO TO DAY IF LESS THAN 10
            $(".joel-date", this).text(((parseInt($(".joel-date", this).text()) < 10) ? "0" : "") + $(".joel-date", this).text());
        });



        /*$(".upcoming-column.left").not($("#hp-content-bottom .upcoming-column.left")).each(function() {
			if($(".joel-month", this).text() != csMonth) {
				csMonth = $(".joel-month", this).text();
				$(this).parent().addClass("mod-events-month-container").prepend('
' + csMonth + '');
			}
		});

        $('.ui-widget.app.upcomingevents .ui-widget-detail .ui-articles .upcoming-column.right .ui-article-description').each(function(){
			$(".sw-calendar-block-time", this).before($(".sw-calendar-block-title",this));
		});*/
	}

	function searchText() {
		//DOC custom mystart search placeholder
	    //SEARCH TEXT
	    $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "I'm looking for...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "I'm looking for...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("I'm looking for...");
			}
		});
	}

    function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r+', '+g+', '+b;
	};

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	};

