        //}

    }

    function canvasGradientMobile() {
    	if($("#hp-slideshow .slideshow-bottom-cont").length) {
            initGradMobile();
        }
        if($(".hp-content.two").length) {
            initContentGrad();
        }
    }

    function initGrad() {

      var canvas = document.getElementById("canvasGrad");
      var ctx = canvas.getContext("2d");

      drawGrad(ctx);
    }

    function initGradMobile() {

      var canvas = document.getElementById("canvasGrad");
      var ctx = canvas.getContext("2d");

      drawGradMobile(ctx);
    }

    function initContentGrad() {

      var canvas = document.getElementById("canvasFooterGrad");
      var ctx = canvas.getContext("2d");

      drawContentGrad(ctx);
    }

    function drawGrad(ctx) {
	  var topGradColor = "rgba("+hexToRgb('#00205B')+",0.4)";
      var bottomGradColor = "rgba("+hexToRgb('#00205B')+",0.8)";

      var gradient;

      // layer1/Compound Path
      ctx.save();
      ctx.beginPath();

      // layer1/Compound Path/Path
      ctx.moveTo(1324.9, 0.1);
      ctx.bezierCurveTo(1303.1, -0.2, 1176.0, 0.5, 1157.8, 1.1);
      ctx.bezierCurveTo(1083.4, 3.6, 1031.5, 7.6, 992.9, 11.6);
      ctx.bezierCurveTo(974.8, 13.5, 896.4, 21.8, 840.6, 33.6);
      ctx.bezierCurveTo(802.0, 41.7, 764.4, 49.9, 726.2, 59.8);
      ctx.bezierCurveTo(690.0, 69.2, 594.3, 96.4, 583.9, 97.7);
      ctx.bezierCurveTo(581.6, 97.9, 467.0, 114.5, 326.6, 97.0);
      ctx.bezierCurveTo(268.7, 89.7, 198.1, 74.7, 160.1, 65.0);
      ctx.bezierCurveTo(157.0, 64.2, 109.2, 54.0, 0.0, 14.5);
      ctx.lineTo(0.0, 227.9);
      ctx.lineTo(1500.0, 227.9);
      ctx.bezierCurveTo(1500.0, 227.9, 1500.1, 6.5, 1500.0, 4.5);
      ctx.bezierCurveTo(1471.7, 3.8, 1342.9, 0.3, 1324.9, 0.1);
      ctx.closePath();

      // layer1/Compound Path/Path
      ctx.moveTo(1499.8, 5.4);
      ctx.bezierCurveTo(1496.8, 5.1, 1339.6, 0.9, 1321.5, 0.7);
      gradient = ctx.createLinearGradient(750.0, 73.2, 750.0, 213.0);
      gradient.addColorStop(0.00, topGradColor);
      gradient.addColorStop(1.00, bottomGradColor);
      ctx.fillStyle = gradient;
      ctx.fill();
      ctx.restore();
    }

    function drawGradMobile(ctx) {
      var fullGradColor = "rgba("+hexToRgb('#00205B')+",1)";

      var gradient;

      // layer1/Compound Path
      ctx.save();
      ctx.beginPath();

      // layer1/Compound Path/Path
      ctx.moveTo(1324.9, 0.1);
      ctx.bezierCurveTo(1303.1, -0.2, 1176.0, 0.5, 1157.8, 1.1);
      ctx.bezierCurveTo(1083.4, 3.6, 1031.5, 7.6, 992.9, 11.6);
      ctx.bezierCurveTo(974.8, 13.5, 896.4, 21.8, 840.6, 33.6);
      ctx.bezierCurveTo(802.0, 41.7, 764.4, 49.9, 726.2, 59.8);
      ctx.bezierCurveTo(690.0, 69.2, 594.3, 96.4, 583.9, 97.7);
      ctx.bezierCurveTo(581.6, 97.9, 467.0, 114.5, 326.6, 97.0);
      ctx.bezierCurveTo(268.7, 89.7, 198.1, 74.7, 160.1, 65.0);
      ctx.bezierCurveTo(157.0, 64.2, 109.2, 54.0, 0.0, 14.5);
      ctx.lineTo(0.0, 227.9);
      ctx.lineTo(1500.0, 227.9);
      ctx.bezierCurveTo(1500.0, 227.9, 1500.1, 6.5, 1500.0, 4.5);
      ctx.bezierCurveTo(1471.7, 3.8, 1342.9, 0.3, 1324.9, 0.1);
      ctx.closePath();

      // layer1/Compound Path/Path
      ctx.moveTo(1499.8, 5.4);
      ctx.bezierCurveTo(1496.8, 5.1, 1339.6, 0.9, 1321.5, 0.7);
      gradient = ctx.createLinearGradient(750.0, 73.2, 750.0, 213.0);
      gradient.addColorStop(0.00, fullGradColor);
      gradient.addColorStop(1.00, fullGradColor);
      ctx.fillStyle = gradient;
      ctx.fill();
      ctx.restore();
    }

    function drawContentGrad(ctx) {
      var gradient;

      // layer1/Compound Path
      ctx.save();
      ctx.beginPath();

      // layer1/Compound Path/Path
      ctx.moveTo(1324.9, 0.1);
      ctx.bezierCurveTo(1303.1, -0.2, 1176.0, 0.5, 1157.8, 1.1);
      ctx.bezierCurveTo(1083.4, 3.6, 1031.5, 7.6, 992.9, 11.6);
      ctx.bezierCurveTo(974.8, 13.5, 896.4, 21.8, 840.6, 33.6);
      ctx.bezierCurveTo(802.0, 41.7, 764.4, 49.9, 726.2, 59.8);
      ctx.bezierCurveTo(690.0, 69.2, 594.3, 96.4, 583.9, 97.7);
      ctx.bezierCurveTo(581.6, 97.9, 467.0, 114.5, 326.6, 97.0);
      ctx.bezierCurveTo(268.7, 89.7, 198.1, 74.7, 160.1, 65.0);
      ctx.bezierCurveTo(157.0, 64.2, 109.2, 54.0, 0.0, 14.5);
      ctx.lineTo(0.0, 227.9);
      ctx.lineTo(1500.0, 227.9);
      ctx.bezierCurveTo(1500.0, 227.9, 1500.1, 6.5, 1500.0, 4.5);
      ctx.bezierCurveTo(1471.7, 3.8, 1342.9, 0.3, 1324.9, 0.1);
      ctx.closePath();

      // layer1/Compound Path/Path
      ctx.moveTo(1499.8, 5.4);
      ctx.bezierCurveTo(1496.8, 5.1, 1339.6, 0.9, 1321.5, 0.7);
      gradient = ctx.createLinearGradient(750.0, 73.2, 750.0, 213.0);
      gradient.addColorStop(0.00, "rgba(255,255,255,.6)");
      gradient.addColorStop(1.00, "rgba(255,255,255,1)");
      ctx.fillStyle = gradient;
      ctx.fill();
      ctx.restore();
    }

