// adds function to hide/show elements based on date.
 function ShowOrHideOnDateTemp(targetDate, idSelector) {

   let idToShowOrHide = document.getElementById(idSelector);

   let todayDate = new Date();
   let targetDateValue = new Date(targetDate);



   if (todayDate >= targetDateValue) {
     $(idToShowOrHide).show();
   } else {
     $(idToShowOrHide).css('display', 'none')
   }
 }





