$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=CKvrxbiplyCyuvzx4SDYEQ%3d%3d&SessionID=169c30d2-f4fc-4293-9f70-f0d60bdd3893&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+8%3a28%3a29+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.28.146';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
