$(window).on('load', function() {
	const facebookLink = 'https://www.facebook.com/HIDOE808';
	const facebookLinkHttps = document.querySelector("[href='https://www.facebook.com/HIDepartmentofEducation']");
	const facebookLinkHttp = document.querySelector("[href='http://www.facebook.com/HIDepartmentofEducation']");
	if (facebookLinkHttps !== null) facebookLinkHttps.href = facebookLink;
	if (facebookLinkHttp !== null) facebookLinkHttp.href = facebookLink;
	
	const vimeoLink = document.querySelector("[href='http://vimeo.com/hawaiipublicschools']");
	if (vimeoLink !== null) vimeoLink.href = 'http://vimeo.com/hidoe';
});

