				'icon4'		: ['Information Technology/1.png', 'SISK12 Portal', 'http://sdm.sisk12.com/vs/', ' _blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
				'icon5'		: ['Information Technology/42.png', 'Board Updates', 'https://www.district65.net/domain/71', ' _blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
