	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			$(window).scroll(parallaxScroll);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMulimediaRotator({
				"imageWidth"		: 725,
				"imageHeight"		: 343,
				"titles"		: "yes",
				"captions"		: "yes",
				"playPause"		: "no",
				"mainControls"		: "no",
				"bullets"		: "yes",
				"bulletControls"		: "no",
				"hoverPause"		: "yes",
				"transitionSpeed"	: 3,
				"transitionDelay"	: 8,
				"onImageLoad"		: function(){ 
								var ssWidth = $(".rs-gallery-container").width();
								var bcWidth = $(".rs-gallery-bullets-outer").width();
								var bcDiff = (ssWidth - bcWidth) - 50;
								var bcOffset = (bcDiff/2);
								$(".rs-gallery-bullets-outer").css("left",bcOffset);
				},
				"onTransitionStart"	: function(){ },
				"onTransitionEnd"	: function(){ },
				"watchVidClick"		: function(){ },
				"closeVidClick"		: function(){ },
				"allLoaded"		: function(){ 
								$(window).resize(function(){
													var ssWidth = $(".rs-gallery-container").width();
										var bcWidth = $(".rs-gallery-bullets-outer").width();
										var bcDiff = (ssWidth - bcWidth) - 50;
										var bcOffset = (bcDiff/2);
										$(".rs-gallery-bullets-outer").css("left",bcOffset);
	               					 	});

                                        $(".district65 #hp-slideshow .rs-gallery-container .rs-gallery-bullets-outer").appendTo('#hp-slideshow .rs-gallery-container');
				}
			});
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }


	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("");
		$(".sw-dropdown-arrow").text("");

		$("#sw-mystart-search #sw-search-button").html("3");
		$("#sw-mystart-search").appendTo("#gb-search");
		$(".sw-mystart-dropdown.schoollist .selector, .sw-mystart-dropdown.schoollist .sw-dropdown-selected").text("Choose School");

		// ********** HEADER ********** //
		//DOC add sitename
		var siteNameOne = jQuery.trim('Evanston/Skokie');
		var siteNameTwo = jQuery.trim('School District 65');
		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 2;
			var siteName = "Evanston/Skokie School District 65";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 2){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
			} else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
			}
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename, #gb-sitename-fixed").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename, #gb-sitename-fixed").prepend("
" + siteNameOne + "");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename, #gb-sitename-fixed").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			//$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
            $("#gb-sitename, #gb-sitename-fixed").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

        $("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){

                // LOWERCASE THE CHANNEL NAME
                var channelText = $("> a span", this).text().toLowerCase();
                // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                if(channelText == "home") {
                    $(this).addClass("home");
                } else if(channelText == "calendar") {
                    $(this).addClass("calendar");
                }

            });

		//AJAX IN DISTRICT CHANNELS ON SCHOOL SITES
		if("district65" != "district65") {
			$("#district-channels #gb-channel-list-ajax, #district-channels-fixed #gb-channel-list-fixed-ajax").load("/district65 #gb-channel-list #sw-channel-list-container", function(){
				//HIDE SUBSITE CHANNLES EXCEPT ABOUT US

				$("#gb-channel-list .sw-channel-item > a > span").each(function() {
					if($(this).text() != "About Us") {
						$(this).parent().parent().hide();
					}
				});
				$("#gb-channel-list-fixed .sw-channel-item > a > span").each(function() {
					if($(this).text() != "About Us") {
						$(this).parent().parent().hide();
					}
				});
				$("#gb-channel-list-ajax .sw-channel-item > a > span").each(function() {
					if($(this).text() == "About Us") {
						$(this).parent().parent().hide();
					}
				});
				$("#gb-channel-list-fixed-ajax .sw-channel-item > a > span").each(function() {
					if($(this).text() == "About Us") {
						$(this).parent().parent().hide();
					}
				});
                $('#gb-channel-list-fixed #channel-navigation .ui-clear').remove();
				$("#district-channels .sw-channel-item, #district-channels-fixed .sw-channel-item").hover(function() {
                	$(this).addClass("hover");
					$(".sw-channel-dropdown",this).show();
					$(this).css({
						"-webkit-box-shadow": "0px -1px 5px 0px rgba(50, 50, 50, 0.75)",
						"-moz-box-shadow":    "0px -1px 5px 0px rgba(50, 50, 50, 0.75)",
						"box-shadow":         "0px -1px 5px 0px rgba(50, 50, 50, 0.75)",
						"behavior": "url(/cms/lib/IL01906289/Centricity/Template/8/scripts/PIE.htc)"
					});
				}, function() {
                	$(this).removeClass("hover");
					$(".sw-channel-dropdown",this).hide();
					$(this).css({
						"-webkit-box-shadow": "none",
						"-moz-box-shadow":    "none",
						"box-shadow":         "none",
					});
				});
			}).show();

		}

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span").text("More ");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}

        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//MOD EVENTS
		$(".ui-hp .upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".ui-hp .upcomingevents .ui-articles");
		$("div.ui-widget.app.upcomingevents .ui-widget-detail .ui-articles li:even").addClass("odd");

		$("a.view-calendar-link span").text("View All");

		$(".hp.column.upper div.ui-widget.app.navigation li").each(function() {
			if ($("a",this).text() == '') {
				$("a",this).parent().addClass("active");
			}
		});

		//CLICK FUNCTION FOR LINKS DROPDOWN
		$("#hp-registration").click(function(e){
			e.preventDefault();
			if(!$(this).hasClass("open")){
				$("#hp-registration-links").slideDown(function(){
					$("#hp-registration").removeClass("closed").addClass("open");
					$(this).removeClass("closed").addClass("open");
				});
			} else {
				$("#hp-registration-links").slideUp(function(){
					$("#hp-registration").removeClass("open").addClass("closed");
					$(this).removeClass("open").addClass("closed");
				});
			}
		}); 

		// ********** FOOTER ********** //
		$("#gb-go-to-top a").click(function(e){
        		e.preventDefault();
        		$("html, body").animate({
				scrollTop: $("body").offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
        	});

		//DOC get Schoolwires footer links
		//var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		//$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);			
	}

    function stickyCalendarFix() {
        var channelHeight = $("#gb-channel-list-outer-fixed").height();

        if ($(window).scrollTop() <= channelHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", channelHeight + "px");
                }
            }
        }
    }



