        // BUILD THE MEGA MENUS
        $("#gb-channel-list #sw-channel-list-container li.sw-channel-item, #gb-channel-list-fixed #sw-channel-list-container li.sw-channel-item").each(function(index){

        	// LOWERCASE THE CHANNEL NAME
            var channelText = $("> a span", this).text().toLowerCase();
            var channelTextReg = $("> a span", this).text();

            // LOWERCASE THE CHANNEL NAME
                // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                if(channelText == "home") {
                    $(this).addClass("home");
                } else if(channelText == "calendar") {
                    $(this).addClass("calendar");
                }

        	//IGNORE HOME, CALENDAR and StAFF CHANNELS
            if(channelText != "home" && channelText != "calendar") {

                // SET THE CHANNELS INDEX
                var index = index;
                var i = $("#sw-channel-list-container li.sw-channel-item.home").length ? $(this).index() - 1 : $(this).index();

                if(jQuery.trim(megaMenuData[i].megaMenuToggle) == "true"){
                    // ADD MEGA CHANNEL CLASS
                    $(this).addClass('mega-channel');
                    $(this).addClass("Channel-" + index);

                    // ADD CHANNEL CATEGORIES
                    var categoryColumn = '
';
                    var textNums = ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen", "twenty"];
                    var channelCats = megaMenuData[i].channelCats;
                    for(var c in channelCats) {
                        if(channelCats[c].channelCatHeader != "") {
                            var catSections = channelCats[c].channelCatSections.split("|");

                            categoryColumn += '
';
                            categoryColumn += 	'
' + channelCats[c].channelCatHeader + '';
                            categoryColumn += 	'';
                                                        for(var s in catSections) {
                                                            $("> .sw-channel-dropdown li", this).each(function(){
                                                                if($("a span", this).text() == jQuery.trim(catSections[s])){
                                                                    //categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                                                    categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                                                    } else {
                                                                        categoryColumn += '>' + jQuery.trim(catSections[s]) + '';
                                                                    }
                                                                }
                                                            });

                                                        }
                            categoryColumn += 	'';
                            categoryColumn += '';
                        }    
                    }
                    categoryColumn +=	'';
                    // ADD STRUCTURE TO THE DOM
                    $(".sw-channel-dropdown", this).html('





');

                    // ADD IMAGE
                    var imgToggle = jQuery.trim(megaMenuData[i].channelImgToggle);
                    var imgColumn = '';
                    var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                    var srcSplit = imgSrc.split("/");
                    var srcSplitLen = srcSplit.length;
                    if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                        imgColumn = '';
                    }
                    if(imgToggle != "false"){
                    	$(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(imgColumn).removeClass("hidden");
                    }

                    $(".sw-channel-dropdown .mega-columns .mega-menu-bottom", this).append(categoryColumn);

                    // ADD CHANNEL TITLE
                    var titleColumn = '
' + jQuery.trim(megaMenuData[i].channelTitle) + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(titleColumn).removeClass("hidden");;

                    // ADD CAPTION
                    var captionColumn = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(captionColumn).removeClass("hidden");;

                    //ADD LINKS
                    var linksColumn = '
';
                    var linksHeader = megaMenuData[i].channelLinksHeader;
                    if(linksHeader !=""){
                        linksColumn += '
' + linksHeader + '
';

                        var channelLinks = megaMenuData[i].channelLinks;
                        for(var l in channelLinks) {
                            if(channelLinks[l].channelLinksHeader != "") {
                                if(channelLinks[l].channelLinkText != ""){
                                    linksColumn +=      '
'+ channelLinks[l].channelLinkText + '';
                                }
                            }     
                        }
                    linksColumn += '';

                    }
                    linksColumn += '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-three", this).append(linksColumn).removeClass("hidden");
                    var linkNum = $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-three li.mega-channel-link",this).length;
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).addClass('link-num-' + linkNum);
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-three", this).addClass('link-num-' + linkNum);
                }   

            }

        });


        $("#gb-channel-list #sw-channel-list-container li.sw-channel-item.mega-channel").each(function(index){
        	var topColumn = $(".mega-menu-top > div:empty",this).length;
            var windowWidth = $(window).width();
            var position = $(this).position();
            var leftPos = position.left;

            if(windowWidth == 1024) {
            	if(topColumn == "0"){
                    $(".sw-channel-dropdown", this).css("width","1024");
                    $(".sw-channel-dropdown",this).css("left",-leftPos);
                }
                if(topColumn == "1"){
                    $(".sw-channel-dropdown", this).css("width","910");
                }
                if(topColumn == "2"){
                    $(".sw-channel-dropdown", this).css("width","625");
                }
            }  else if(windowWidth == 768) {
            	if(topColumn == "0"){
                    $(".sw-channel-dropdown", this).css("width","768");
                    $(".sw-channel-dropdown",this).css("left",-leftPos - 14);
                }
                if(topColumn == "1"){
                    $(".sw-channel-dropdown", this).css("width","768");
                    $(".sw-channel-dropdown",this).css("left",-leftPos - 14);
                }
                if(topColumn == "2"){
                    $(".sw-channel-dropdown", this).css("width","625");
                }
            }

            var position = $(this).position();
            var leftPos = position.left;

            var channelWidth = $("#gb-channel-list-inner").width();
            var channelOverflow = (windowWidth - channelWidth)/2;
            var dropdownWidth = $(".sw-channel-dropdown",this).width() + 70;
            var isOverflowing = (channelWidth - leftPos) + channelOverflow;
            var offset = dropdownWidth - isOverflowing;

            console.log("Dropdown width: " + dropdownWidth);
            console.log("overflowing: " + isOverflowing);
            console.log("Offset: " + offset);

            if(dropdownWidth > isOverflowing) {
            	$(".sw-channel-dropdown",this).css("left",-offset);
            } else {
            	$(".sw-channel-dropdown",this).css("left","0px");
            }

             $(window).resize(function(){
             	var position = $(this).position();
                var leftPos = position.left;
                var windowWidth = $(window).width();
                var channelWidth = $("#gb-channel-list-inner").width();
                var channelOverflow = (windowWidth - channelWidth)/2;
                var dropdownWidth = $(".sw-channel-dropdown",this).width() + 70;
                var isOverflowing = (channelWidth - leftPos) + channelOverflow;
                var offset = dropdownWidth - isOverflowing;

                if(dropdownWidth > isOverflowing) {
                    $(".sw-channel-dropdown",this).css("left",-offset);
                } else {
                    $(".sw-channel-dropdown",this).css("left","0px");
                }
             });

         });

         $("#gb-channel-list-fixed #sw-channel-list-container li.sw-channel-item.mega-channel").each(function(index){
        	var topColumn = $(".mega-menu-top > div:empty",this).length;
            var windowWidth = $(window).width();
            var position = $(this).position();
            var leftPos = position.left;

            if(windowWidth == 1024) {
            	if(topColumn == "0"){
                    $(".sw-channel-dropdown", this).css("width","1024");
                    $(".sw-channel-dropdown",this).css("left",-leftPos);
                }
                if(topColumn == "1"){
                    $(".sw-channel-dropdown", this).css("width","910");
                }
                if(topColumn == "2"){
                    $(".sw-channel-dropdown", this).css("width","625");
                }
            }  else if(windowWidth == 768) {
            	if(topColumn == "0"){
                    $(".sw-channel-dropdown", this).css("width","768");
                    $(".sw-channel-dropdown",this).css("left",-leftPos);
                }
                if(topColumn == "1"){
                    $(".sw-channel-dropdown", this).css("width","768");
                    $(".sw-channel-dropdown",this).css("left",-leftPos);
                }
                if(topColumn == "2"){
                    $(".sw-channel-dropdown", this).css("width","625");
                }
            }

            var position = $(this).position();
            var leftPos = position.left;

            var channelWidth = $("#gb-channel-list-inner-fixed").width();
            var channelOverflow = (windowWidth - channelWidth)/2;
            var dropdownWidth = $(".sw-channel-dropdown",this).width() + 70;
            var isOverflowing = (channelWidth - leftPos) + channelOverflow;
            var offset = dropdownWidth - isOverflowing;


            if(dropdownWidth > isOverflowing) {
            	$(".sw-channel-dropdown",this).css("left",-offset);
            } else {
            	$(".sw-channel-dropdown",this).css("left","0px");
            }

             $(window).resize(function(){
             	var position = $(this).position();
                var leftPos = position.left;
                var windowWidth = $(window).width();
                var channelWidth = $("#gb-channel-list-inner-fixed").width();
                var channelOverflow = (windowWidth - channelWidth)/2;
