        function ajax(){
    	if("district65" != "district65"){
    		$.ajax({
                url: "/district65",
                complete: function(data, status){

                    var openComment = "/* district-start */";

                    var districtChannels = eval(data.responseText.slice((data.responseText.indexOf(openComment)) + openComment.length, data.responseText.indexOf('/* district-end */')));




            // BUILD THE MEGA MENUS
            $("#gb-channel-list-ajax #sw-channel-list-container li.sw-channel-item, #gb-channel-list-fixed-ajax #sw-channel-list-container li.sw-channel-item").each(function(index){

            	$("#gb-channel-list-ajax ul.sw-channel-list li.hidden-channel, #gb-channel-list-fixed-ajax ul.sw-channel-list li.hidden-channel").remove();

                // LOWERCASE THE CHANNEL NAME
                var channelText = $("> a span", this).text().toLowerCase();
                var channelTextReg = $("> a span", this).text();

                // LOWERCASE THE CHANNEL NAME
                    // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                    if(channelText == "home") {
                        $(this).addClass("home");
                    } else if(channelText == "calendar") {
                        $(this).addClass("calendar");
                    }

                //IGNORE HOME, CALENDAR and StAFF CHANNELS
                if(channelText != "home" && channelText != "calendar") {

                    // SET THE CHANNELS INDEX
                    var index = index;
                    var i = $("#sw-channel-list-container li.sw-channel-item.home").length ? $(this).index() - 1 : $(this).index();

                    if(jQuery.trim(districtChannels[i].megaMenuToggle) == "true"){
                        // ADD MEGA CHANNEL CLASS
                        $(this).addClass('mega-channel');
                        $(this).addClass("Channel-" + index);

                        // ADD CHANNEL CATEGORIES
                        var categoryColumn = '
';
                        var textNums = ["one", "two", "three", "four", "five", "six", "seven", "eight", "nine", "ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen", "twenty"];
                        var channelCats = districtChannels[i].channelCats;
                        for(var c in channelCats) {
                            if(channelCats[c].channelCatHeader != "") {
                                var catSections = channelCats[c].channelCatSections.split("|");

                                categoryColumn += '
';
                                categoryColumn += 	'
' + channelCats[c].channelCatHeader + '';
                                categoryColumn += 	'';
                                                            for(var s in catSections) {
                                                                $("> .sw-channel-dropdown li", this).each(function(){
                                                                    if($("a span", this).text() == jQuery.trim(catSections[s])){
                                                                        //categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                                                        categoryColumn += '
' + jQuery.trim(catSections[s]) + '';
                                                                        } else {
                                                                            categoryColumn += '>' + jQuery.trim(catSections[s]) + '';
                                                                        }
                                                                    }
                                                                });

                                                            }
                                categoryColumn += 	'';
                                categoryColumn += '';
                            }    
                        }
                        categoryColumn +=	'';
                        // ADD STRUCTURE TO THE DOM
                        $(".sw-channel-dropdown", this).html('





');

                        // ADD IMAGE
                        var imgToggle = jQuery.trim(districtChannels[i].channelImgToggle);
                        var imgColumn = '';
                        var imgSrc = jQuery.trim(districtChannels[i].channelImg);
                        var srcSplit = imgSrc.split("/");
                        var srcSplitLen = srcSplit.length;
                        if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                            imgColumn = '';
                        }
                        if(imgToggle != "false"){
                            $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(imgColumn).removeClass("hidden");
                        }

                        $(".sw-channel-dropdown .mega-columns .mega-menu-bottom", this).append(categoryColumn);

                        // ADD CHANNEL TITLE
                        var titleColumn = '
' + jQuery.trim(districtChannels[i].channelTitle) + '';
                        $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(titleColumn).removeClass("hidden");;

                        // ADD CAPTION
                        var captionColumn = '
' + jQuery.trim(districtChannels[i].channelCaption) + '';
                        $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(captionColumn).removeClass("hidden");;

                        //ADD LINKS
                        var linksColumn = '
';
                        var linksHeader = districtChannels[i].channelLinksHeader;
                        if(linksHeader !=""){
                            linksColumn += '
' + linksHeader + '
';

                            var channelLinks = districtChannels[i].channelLinks;
                            for(var l in channelLinks) {
                                if(channelLinks[l].channelLinksHeader != "") {
                                    if(channelLinks[l].channelLinkText != ""){
                                        linksColumn +=      '
'+ channelLinks[l].channelLinkText + '';
                                    }
                                }     
                            }
                        linksColumn += '';

                        }
                        linksColumn += '';
                        $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-three", this).append(linksColumn).removeClass("hidden");
                        var linkNum = $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-three li.mega-channel-link",this).length;
                        $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).addClass('link-num-' + linkNum);
                        $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-three", this).addClass('link-num-' + linkNum);

                    }   

                }

                var topColumn = $(".mega-menu-top > div:empty",this).length;
                var windowWidth = $(window).width();
                var position = $(this).position();
                var leftPos = position.left;

                if(windowWidth == 1024) {
                    if(topColumn == "0"){
                        $(".sw-channel-dropdown", this).css("width","1024");
                        $(".sw-channel-dropdown",this).css("left",-leftPos);
                    }
                    if(topColumn == "1"){
                        $(".sw-channel-dropdown", this).css("width","910");
                    }
                    if(topColumn == "2"){
                        $(".sw-channel-dropdown", this).css("width","625");
                    }
                }  else if(windowWidth == 768) {
                    if(topColumn == "0"){
                        $(".sw-channel-dropdown", this).css("width","768");
                        $(".sw-channel-dropdown",this).css("left",-leftPos);
                    }
                    if(topColumn == "1"){
                        $(".sw-channel-dropdown", this).css("width","768");
                        $(".sw-channel-dropdown",this).css("left",-leftPos);
                    }
                    if(topColumn == "2"){
                        $(".sw-channel-dropdown", this).css("width","625");
                    }
                }

                var position = $(this).position();
                var leftPos = position.left;

                var channelWidth = $("#gb-channel-list-ajax").width();
                var channelOverflow = (windowWidth - channelWidth)/2;
                var dropdownWidth = $(".sw-channel-dropdown",this).width() + 70;
                var isOverflowing = (channelWidth - leftPos) + channelOverflow;
                var offset = dropdownWidth - isOverflowing;

				console.log("channel width: " + channelWidth);
                console.log("channel overflow: " + channelOverflow);
                console.log("left pos: " + leftPos);
                console.log("Dropdown width: " + dropdownWidth);
                console.log("overflowing: " + isOverflowing);
                console.log("Offset: " + offset);

                if(dropdownWidth > isOverflowing) {
                    $(".sw-channel-dropdown",this).css("left",-offset);
                } else {
                    $(".sw-channel-dropdown",this).css("left","0px");
                }

                $(window).resize(function(){
                  var position = $(this).position();
                  var leftPos = position.left;
                  var windowWidth = $(window).width();
                  var channelWidth = $("#gb-channel-list-ajax").width();
                  var channelOverflow = (windowWidth - channelWidth)/2;
                  var dropdownWidth = $(".sw-channel-dropdown",this).width() + 70;
                  var isOverflowing = (channelWidth - leftPos) + channelOverflow;
                  var offset = dropdownWidth - isOverflowing;

                  if(dropdownWidth > isOverflowing) {
                      $(".sw-channel-dropdown",this).css("left",-offset);
                  } else {
                      $(".sw-channel-dropdown",this).css("left","0px");
                  }
                });

            });



            }

            });

        }    
    }

    function megaMenuAccessibility() {
    	var _this = this;


        $(".mega-channel .mega-columns .mega-menu-top-col-three li.mega-channel-link a").on("keydown", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {

                case _this.KeyCodes.left:
                case _this.KeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON

                        $(this).closest("li.sw-channel-item").find("> a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case _this.KeyCodes.right:
                case _this.KeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(".mega-channel-cat.one .mega-channel-cat-section-list.one li:first-child > a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }
                break;
            }
        });

        $(".mega-channel-cat .mega-channel-cat-section-list li a").on("keydown", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {

                case _this.KeyCodes.left:
                case _this.KeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON

                        $(this).parent().parent().parent().prev(".mega-channel-cat").find("ul > li:last-child > a").focus();
                    } else if($(this).parent().parent().parent().is(":first-child") && $(this).parent().is(":first-child")) {
                    	$(".mega-channel .mega-columns .mega-menu-top-col-three li.mega-channel-link:last-child a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }


                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case _this.KeyCodes.right:
                case _this.KeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).parent().parent().parent().next(".mega-channel-cat").find("ul > li:first-child > a").focus();
                    } else if($(this).parent().parent().parent().is(":last-child") && $(this).parent().is(":last-child")) {
                    	$(this).closest("li.sw-channel-item").find("> a").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).parent().next("li").find("> a").focus();
                    }

                break;
            }
        });

    }

	function buildStyleSheet(){
		var primaryColor50 = "rgba(" + hexToRgb('#014f7f') + ", .50)";
		var primaryColor20 = "rgba(" + hexToRgb('#014f7f') + ", .20)";
		var primaryColor100 = "rgba(" + hexToRgb('#014f7f') + ", 1)";
		var secondaryColor00 = "rgba(" + hexToRgb('#4EA2DA') + ", 0)";
		var secondaryColor50 = "rgba(" + hexToRgb('#4EA2DA') + ", .50)";
		var secondaryColor52 = "rgba(" + hexToRgb('#4EA2DA') + ", .52)";
		var secondaryColor20 = "rgba(" + hexToRgb('#4EA2DA') + ", .20)";
		var primaryColor = "#014f7f";
		var primaryColorRep = "#014f7f";
		primaryColorRep = primaryColor.replace('#', '');
		var secondaryColor = "#4EA2DA";
		secondaryColor = secondaryColor.replace('#', '');

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'div.sw-mystart-dropdown {' +
					'	background-color: ' + secondaryColor52 + ';' +
					'	-pie-background: ' + secondaryColor52 + ';' +
					'}' +
					'#sw-mystart-inner #rs-mystart-menu-btn {' +
					'	background: ' + secondaryColor52 + ';' +
					'	-pie-background: ' + secondaryColor52 + ';' +
					'}' +
					'#gb-channel-list-bottom {' +
					'	background: -moz-linear-gradient(left,  ' + secondaryColor50 + ' 0%, #4EA2DA 25%, #4EA2DA 75%, ' + secondaryColor50 + ' 100%);' +
					'	background: -webkit-gradient(linear, left top, right top, color-stop(0%,' + secondaryColor50 + '), color-stop(25%,#4EA2DA), color-stop(75%,#4EA2DA), color-stop(100%,' + secondaryColor50 + '));' + 
					'	background: -webkit-linear-gradient(left,  ' + secondaryColor50 + ' 0%,#4EA2DA 25%,#4EA2DA 75%,' + secondaryColor50 + ' 100%);' +
					'	background: -o-linear-gradient(left,  ' + secondaryColor50 + ' 0%,#4EA2DA 25%,#4EA2DA 75%,' + secondaryColor50 + ' 100%);' +
					'	background: -ms-linear-gradient(left,  ' + secondaryColor50 + ' 0%,#4EA2DA 25%,#4EA2DA 75%,' + secondaryColor50 + ' 100%);' +
					'	background: linear-gradient(to right,  ' + secondaryColor50 + ' 0%,#4EA2DA 25%,#4EA2DA 75%,' + secondaryColor50 + ' 100%);' +
					'	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr="#80' + secondaryColor + '", endColorstr="#80' + secondaryColor + '",GradientType=1 );' +
					'}' +
					'#hp-social-icons .icn {' +
					'	background: -moz-radial-gradient(center, ellipse cover,  ' + primaryColor50 + ' 0%, #014f7f 100%);' +
					'	background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,' + primaryColor50 + '), color-stop(100%,#014f7f));' +
					'	background: -webkit-radial-gradient(center, ellipse cover,  ' + primaryColor50 + ' 0%,#014f7f 100%);' +
					'	background: -o-radial-gradient(center, ellipse cover,  ' + primaryColor50 + ' 0%,#014f7f 100%);' +
					'	background: -ms-radial-gradient(center, ellipse cover,  ' + primaryColor50 + ' 0%,#014f7f 100%);' +
					'	background: radial-gradient(ellipse at center,  ' + primaryColor50 + ' 0%,#014f7f 100%);' +
					'	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr="#80' + primaryColorRep + '", endColorstr="'+ primaryColor100 + '",GradientType=1 );' +
					'}' +
					'.hp.column.upper div.ui-widget.app.navigation li:after{' +
					'	background: ' + secondaryColor20 + ';' +
					'	-pie-background: ' + secondaryColor20 + ';' +
					'}'
			//console.log(dynStyles);
			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search");
			}
		});
	}

