	function jsMediaQueries(){
		switch(csGetBreakPoint()) {
			case "desktop":
				photoGallery();
				appViewReset();
			break;
			case "768":
				photoGallery();
				appView();
			break;
			case "640":
				photoGallery();
				appView();
			break;
			case "480":
				appView();
			break;
			case "320":
				appView();
			break;
		}
	}

	function photoGallery() {
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
			        // PHOTO GALLERY ROTATOR
			        $("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
			                "imageWidth"                : 710,
			                "imageHeight"               : 300,
			                "efficientLoad"             : "no",
			                "appHeader"                 : "no",
			                "titles"                    : "yes",
			                "captions"                  : "yes",
			                "playPause"                 : "yes",
			                "mainControls"              : "yes",
			                "galleryOverlay"            : "no",
			                "linkedElement"             : ["image"], // ["image", "title", "overlay", "all"]
			                "contMoreLink"              : "no",
			                "contMoreLinkText"          : "Read More",
			                "descMoreLink"              : "yes",
			                "descMoreLinkText"          : "Read More",
			                "bullets"                   : "no",
			                "bulletControls"            : "no",
			                "thumbnails"                : "no",
			                "thumbnailWidth"            : 122,
			                "thumbnailHeight"           : 53,
			                "thumbnailSlideDirection"   : "horizontal", // horizontal or vertical
			                "hoverPause"                : "yes",
			                "fullScreenRotator"         : "no", // image dimensions work well at 1500x915
			                "fullScreenBreakpoints"     : ["960", "768", "640", "480", "320"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
			                "autoRotate"                : "yes",
			                "transitionType"            : "slide", // fade or slide
			                "transitionSpeed"           : 2,
			                "onImageLoad"               : function() { },
			                "allImagesLoaded"           : function() { },
			                "onThumbnailLoad"           : function() { },
			                "allThumbnailsLoaded"       : function() { },
			                "onTransitionStart"         : function() { },
			                "onTransitionEnd"           : function() { },
			                "allLoaded"                 : function() {
			                	$("#hp-slideshow .ui-widget-detail").append($("#hp-slideshow .rs-photo-gallery-control.back")).append($("#hp-slideshow .rs-photo-gallery-control.next"));
			                	$("#hp-slideshow .rs-photo-gallery-control.play-pause").insertBefore($("#hp-slideshow .rs-photo-gallery-desc-more-link"));
			                	$("#hp-slideshow .rs-photo-gallery-desc").children().wrapAll('
');
			                }
			        });
			}
		});
	}

	function appView(){
		if(!appViewSet) {
			// MAKE HOMEPAGE APPS COLLAPSIBLE FOR SMALL MOBILE DEVICES
			if(!$(".hp-content div.ui-widget.app.active").length){
				$(".hp-content.top div.ui-widget.app:first").addClass("active");
			}

			// DO NOT ADD APP INTO ACCORDION IF IT DOES NOT HAVE A HEADER
			$(".hp-content div.ui-widget.app").each(function() {
				if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
					$(this).addClass("no-header");
				}
			});

			// ACCORDION HEADER CLICK
			$(".hp-content div.ui-widget-header").click(function(){
				if($(this).parent().hasClass("active")) {
					$(this).parent().removeClass("active");
				} else {
					var clickedApp = $(this).parent().parent().attr("id");
					$(this).parent().addClass("active");
				}
			});

			appViewSet = 1;
		}
	}

	function appViewReset() {
		if(appViewSet) {
			$(".hp-content div.ui-widget-header").unbind("click");

			appViewSet = 0;
		}
	}	

	function globalIcons() {
		head.ready("globalIcons", function() {
			$("#gb-global-icons").creativeIcons({
			        "iconNum"      : "5", // NUMBER OF ICONS
			        "iconColor"    : "#FFF", // ICON COLOR
