	function dynamicContent(){
		// ********** MYSTART ********** //
		// MOVE MYSTART BUTTONS

			if($(".sw-mystart-button.register").length){
				$(".sw-mystart-button.register a span").text("MyView Sign Up");
				$("#gb-mystart-buttons").append($(".sw-mystart-button.register"));
			}
			$(".sw-mystart-button.signin a span").text("MyView Sign In");
			$("#gb-mystart-buttons").append($(".sw-mystart-button.signin"));


		// MOVE DISTRICT HOME BUTTON
		$("#gb-mystart-buttons").append($(".sw-mystart-button.home"));
		// CLONE SCHOOL LIST
		$("#gb-school-list").append($(".sw-mystart-dropdown.schoollist .sw-dropdown-list").clone());


		// ********** HEADER ********** //
		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/IL01904797/Centricity/Template/GlobalAssets/images///logos/White Background Logo Small.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}
		// ADD SITENAME
		if("false" == "true") {
			var siteNameOne = jQuery.trim("");
			var siteNameTwo = jQuery.trim("Home of the Jaguars");
			if((siteNameOne != "") && (siteNameTwo == "")) {
				$("#gb-sitename").prepend("
" + siteNameOne + "");
			} else if((siteNameOne == "") && (siteNameTwo != "")) {
				$("#gb-sitename").prepend("
" + siteNameTwo + "");
			} else if((siteNameOne != "") && (siteNameTwo != "")) {
				$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "");
			}
		}
		// SCHOOL LIST BUTTON CLICK
		$(".global-icon.schools").click(function(e) {
			e.preventDefault();
			$("#gb-school-list").toggleClass("open");
		});
		$(".global-icon.schools").hover(function() {}, function() {
			$("#gb-school-list").removeClass("open");
		});
		$("#gb-school-list li a").click(function(e) {
			e.stopPropagation();
		});


		// ********** CHANNEL BAR ********** //
		// UNBIND SYSTEM CHANNEL CLICK
		$("li.sw-channel-item").each(function() {
			if($("a span", this).text().toLowerCase() == "home") {
				$(this).addClass("home");
			}
			if($("a span", this).text().toLowerCase() == "calendar") {
				$(this).addClass("calendar");
			}
		});

		$(".sw-channel-item > a").not($(".sw-channel-item.home > a")).not($(".sw-channel-item.calendar > a")).click(function(e){
			e.preventDefault();
		});

		// ********** BODY ********** //
		// RENAME FIRST BREADCRUMB
		$("li.ui-breadcrumb-first > a > span").text("Home");
		// MORE LINK TEXT
		$("a.more-link span").text("View All");
		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();
		// CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	// ADJUST SIGN IN PAGE
        	if($("#swlogin").length){
        		$("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	// ADJUST REGISTER PAGE
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}
		// ADJUST SEARCH RESULTS PAGE
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}
        	// ADD READ FULL STORY BUTTON TO EACH HEADLINE
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$('Read More').insertAfter($(".ui-article-description", this));
        		});
        	});
        	// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        	$("div.ui-widget.app .ui-widget-detail img")
        		.not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        		.not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        		.each(function() {
        			if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
					$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
				}
		});
		// CHECK FOR TWITTER FEED IN COLUMN FIVE
		$(".hp.five .ui-widget.app").each(function() {
			if($(".twitter-timeline", this).length) {
				$(".ui-widget-header", this).append('');
			}
		});


		// ********** FOOTER ********** //
		// ADD FOOTER LOGO
		var footerLogoSrc = jQuery.trim("/cms/lib/IL01904797/Centricity/Template/GlobalAssets/images//logos/Jaguar Small.png");
		var footerSrcSplit = footerLogoSrc.split("/");
		var footerSrcSplitLen = footerSrcSplit.length;
		if((footerLogoSrc != "") && (footerSrcSplit[footerSrcSplitLen - 1] != "default-man.jpg")) {
			$(".gb-footer.top").append('');
		} else {
			$(".gb-footer.top").append('');
		}

		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));

		// BUILD GOOGLE MAP URL
		var mapURL = "6950 N. East Prairie Road Lincolnwood IL 60712";
		mapURL = mapURL.replace(/ /g, "+");
		$("#gb-footer-map-link").attr("href", "//www.google.com/maps/place/" + mapURL);	
	}

	function stickyHeader() {
		if(!isMyViewPage) {
			switch(csGetBreakPoint()) {
				case "desktop":
					var gbHeaderOffset = $("#gb-mystart").height() + $("#gb-header").height();
					if ($(window).scrollTop() <= gbHeaderOffset) {
						if(csHeaderIsSticky) {
							$("#gb-mystart-outer").removeClass("sticky").addClass("no-sticky");
							$("#gb-header-outer").removeAttr("style");

							csHeaderIsSticky = 0;
						}
					} else {
						if(!csHeaderIsSticky) {
							$("#gb-mystart-outer").removeClass("no-sticky").addClass("sticky");
							$("#gb-header-outer").css("padding-top", "35px"); 

							csHeaderIsSticky = 1;
						}
					}
				break;
				case "768": case "640": case "480": case "320":
					if(csHeaderIsSticky == 1) {
						$("#gb-mystart-outer").removeClass("sticky").addClass("no-sticky");
						$("#gb-header-outer").removeAttr("style");

						csHeaderIsSticky = 0;
					}
				break;
			}
		}
	}

	function modEvents(){
		head.ready("modEvents", function() {
			$(".upcomingevents").each(function() {
				if (!$(".upcomingevents .joel-month", this).length) {
					$(this).modEvents({
						columns     : "yes",
						monthLong   : "no",
						dayWeek     : "no"
					});

					$(".ui-article", this).each(function(){
						if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
							var moveArticle = $(this).html();
							$(this).parent().prev().children().children().next().append(moveArticle);
							$(this).parent().remove();
						}
					});
				}

				// MOVE CALENDAR LINK
				$(".view-calendar-link span", this).text("View All");
				$(".ui-widget-header", this).append($(".view-calendar-link", this).clone());
			});
		});
	}

	function searchText() {
	        $(".gb-search-button").click(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-form").submit(function(e){
		    e.preventDefault();
		    var searchForm = $(this).parent();
		    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $(".gb-search-input", searchForm).val();
		});
		$(".gb-search-input").focus(function() {
		    if($(this).val() == "I am here to find...") {
		        $(this).val("");
		    }
		});
		$(".gb-search-input").blur(function() {
		    if($(this).val() == "") {
		        $(this).val("I am here to find...");
		    }
		});
	}

	function calendarListView(){
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}

