    //Function to the css rule
        function checkSize(){
            admin_bar = jQuery('#wpadminbar');
            height_to_add = jQuery('#top-alert-section').outerHeight(true);
            height_to_add = 60 + height_to_add;
            var col = jQuery('main.rightcol');
            if (col.css("position") == "static" ) {
                col.css('margin-top', height_to_add+'px');
                if (admin_bar.length) {
                    //add the 46 to the alerts
                    jQuery('#top-alert-section').css('top', '106px')
                }
            } else {
                col.css('margin-top', '0px');
                if (admin_bar.length) {
                    jQuery('#top-alert-section').css('top', '90px')
                }
            }
        }
        function adjustCSS(){
            admin_bar = jQuery('#wpadminbar');
            height_to_add = jQuery('#top-alert-section').outerHeight(true);
            height_to_add = 60 + height_to_add;
            if (admin_bar.length) {
                height_to_add = height_to_add + 32;
                //add the 32 to the alerts
                jQuery('#top-alert-section').css('top', '92px')
            } else {
                jQuery('#top-alert-section').css('top', '60px')
            }
            checkSize(height_to_add);
            jQuery('nav#left').css('top', height_to_add+'px');
            jQuery('#slider').css('margin-top', height_to_add+'px');

        }
        if (window.attachEvent) {window.attachEvent('onload', adjustCSS);}
        else if (window.addEventListener) {window.addEventListener('load', adjustCSS, false);}
        else {document.addEventListener('load', adjustCSS, false);}
        if (window.attachEvent) {window.attachEvent('resize', checkSize);}
        else if (window.addEventListener) {window.addEventListener('resize', checkSize, false);}
        else {document.addEventListener('resize', checkSize, false);}

div.script:
				window.maybeSlide = function(){
					var imgs = Array.prototype.slice.apply(document.querySelectorAll('#slider .image'));
					var slideIndex = 0
					function slide(img){
						var prv = imgs[img - 1 >= 0 ? img - 1 : imgs.length - 1]
						var img = imgs[img]
						if (!img.style.backgroundImage){
							i = new Image()
							i.src = img.dataset ? img.dataset.imageUrl : img.getAttribute('data-image-url')
							i.onload = function(){
								img.style.backgroundImage = 'url("'+i.src+'")'
								prv.classList.add('fade')
								img.classList.remove('fade')
							}
						}
						else {
							prv.classList.add('fade')
							img.classList.remove('fade')
						}
					}
			   
					window.slideStop = function(){
						clearInterval(window.slideInt)
						window.slideInt = null
					}
					window.slideStart = function(){
						if (window.slideInt) return
						slide(slideIndex)
						slideIndex = ((slideIndex + 1) > (imgs.length - 1)) ? 0 : slideIndex + 1
						window.slideInt = setInterval(function(){
							slide(slideIndex)
							slideIndex = ((slideIndex + 1) > (imgs.length - 1)) ? 0 : slideIndex + 1
						}, 8000)
					}
					if ( root_url && window.location.href == root_url+'/' && window.screen.width	 > 767)
						slideStart()
					else slideStop()
                }
				window.addEventListener('load', maybeSlide)
				window.addEventListener('resize', maybeSlide)

