jQuery(function($) {
                    //Go to search function
                    function goToSearch() {
                        /*alert('search used');*/
                        if ($('.st-search-query').val() != '') {
                            setTimeout(function () { window.location.href = '/search/?searchtext=' + $('.st-search-query').val(); }, 500);
                        }
                        if ($('.st-mobile-search-query').val() != '') {
                            setTimeout(function () { window.location.href = '/search/?searchtext=' + $('.st-mobile-search-query').val(); }, 500);
                        }
                    }
                    $(document).ready(function () {
                        //Search Input Enter Key Press
                        $('.st-search-query').keypress(function (event) {
                            var keycode = (event.keyCode ? event.keyCode : event.which);
                            if (keycode == '13') {
                                //Enter key in text box
                                goToSearch();
                                return false;
                            }
                        });
                        //Search Button click
                        $('.btn-search').click(function () {
                            goToSearch();
                            return false;
                        });
                        //Search Mobile Button click
                        $('.mobile-btn-search').click(function () {
                            goToSearch();
                            return false;
                        });
                    });
                });
