section.script:
function googleTranslateElementInit() {
  var x = window.matchMedia("(max-width: 992px)");
  googleMediaInit(x);
  x.addListener(googleMediaInit)
}

function googleMediaInit(x) {
  if (x.matches) { // If media query matches
    new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, gaTrack: true, gaId: 'UA-1499039-1'}, 'google_translate_element_mobile');
  } else {
    new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, gaTrack: true, gaId: 'UA-1499039-1'}, 'google_translate_element');
  }
}

