	function trimUrl(url)     
{     
    return url.replace(/\/$/, "");
} 
var homeRoutes = ['','/eup','/','/Page/1','/default.aspx','/site','/site/default.aspx'];
if($.inArray(trimUrl(location.pathname), homeRoutes) < 0){
			$('#gb-budget-information-cont').css('display','none');
		} 
		searchText();
		calendarListView();
        $('#gb-channel-list .sw-channel-item[aria-haspopup="true"]').each(function(k, v){
			var newLeftPos = $('.sw-channel-dropdown', v).outerWidth() - $('> a', v).outerWidth();
            newLeftPos = newLeftPos/2;

            $('.sw-channel-dropdown', v).css('left', '-' + newLeftPos + 'px');
		});
	});

