	function pgScreenSize(){
		var getWidth = $(window).width();

		if (getWidth >= 1024) { //Desktop View
        	if( $('#gb-header #gb-search-cont').length == 0 ){
            	//move search container back into the header
                $('#gb-search-cont').appendTo($('#gb-header .right'));
                $('#gb-global-icons-cont').appendTo($('#gb-header .right'));
            }

            if($('.gb.footer.two #gb-social-media-icons').length == 0){
                $('#gb-social-media-icons').appendTo($('.gb.footer.two'));
            }

			multimediaRotator();
		}else{ //Mobile BPs
        	if( $('#gb-channel-list #gb-search-cont').length == 0 ){
            	//move search container into the channel bar
                $('#gb-search-cont').appendTo($('#gb-channel-list'));
                $('#gb-global-icons-cont').appendTo($('#gb-channel-list'));
            }

            if( getWidth <= 1024 && getWidth > 640 ){
            	if($('#gb-footer-bottom #gb-social-media-icons').length == 0){
                	$('#gb-social-media-icons').appendTo($('#gb-footer-bottom .social-media-mobile-holder'));
                }
            }else if( getWidth <= 640 ){
				if($('.gb.footer.one > .social-media-mobile-holder').children().length == 0){
                	$('#gb-social-media-icons').appendTo($('.gb.footer.one > .social-media-mobile-holder'));
                }
			}

            if(getWidth >= 480){
				multimediaRotator();

			}
            if( getWidth < 640 ){
            	appView();
            }

		}
	}

	function multimediaRotator(){
		if(!$("#sw-content-container13.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container13.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 610,
		                "imageHeight"             : 480,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
