		                "onTransitionStart"       : function() { },
		                "onTransitionEnd"         : function() { },
		                "watchVidClick"           : function() { },
		                "closeVidClick"           : function() { },
		                "allLoaded"               : function() { 

                        	$('#hp-photo-gallery-app-cont').height( $('#hp-photo-gallery-cont').height() );

                            $(window).resize(function(e){
								$('#hp-photo-gallery-app-cont').height( $('#hp-photo-gallery-cont').height() );
                            });

                            $('#hp-slideshow .rs-gallery-desc').attr('tabindex', 0);

                            $('#hp-slideshow .rs-gallery-desc').hover(function(e){ //in
                            	$(this).addClass('open-menu');
                            	$('#hp-slideshow .rs-gallery-caption').stop(true, true).slideDown();
                            },
                            function(e){ //out
                            	$(this).removeClass('open-menu');
                            	$('#hp-slideshow .rs-gallery-caption').slideUp();
                            })
                            .focusin(function(e){
                            	$(this).addClass('open-menu');
                            	$('#hp-slideshow .rs-gallery-caption').stop(true, true).slideDown();
                            })
                            .focusout(function(e){
                            	$(this).removeClass('open-menu');
                            	$('#hp-slideshow .rs-gallery-caption').slideUp();
                            });
                        }
			});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "no",
	            dayWeek     : "no"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });

        var extraCalendarOptionHtml = '
 \

Select a Calendar \
                                          ';
        var extraCalendarOptionData = [
            ['Calendar 1', '#'],
            ['Calendar 2', '#'],
            ['Calendar 3', '#'],
            ['Calendar 4', '#']
        ];
        var optionCount = 0;
        $.each(extraCalendarOptionData, function(k, v){
            if( v[1] != "" ){
                extraCalendarOptionHtml += '
' + v[0] + '';
                optionCount++;
            }
        });
        extraCalendarOptionHtml += '';

        if( optionCount > 0 ) $('.hp.row.two .upcomingevents .ui-widget-footer').append(extraCalendarOptionHtml);
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else{
				if( $('.ui-widget-header h1 .accordion-handle', this).length <= 0){
					$('.ui-widget-header h1', this).append('');
				}
			}
		});

		//DOC accordion header click
		$("#hp-content div.ui-widget-header").off('click').click(function(){
        	if( $(this).parent().hasClass("active") ){ //If they click the same header again it will hide
            	$('.accordion-handle', this).removeClass('point-down');
                $(this).parent().removeClass("active");
            }else{
                var clickedApp = $(this).parent().parent().attr("id");
                $("#hp-content div.ui-widget.app.active .ui-widget-header .accordion-handle").removeClass('point-down');
                $("#hp-content div.ui-widget.app.active").removeClass("active"); 
                $(this).parent().addClass("active");
                $("#hp-content div.ui-widget.app.active .ui-widget-header .accordion-handle").addClass('point-down');
                smoothScroll("#" + clickedApp, 3);
            }
		});
	}	

