			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

    function toggleHeadlineCards( obj ){
    	if( $(obj).hasClass('open-menu') ){
            $(obj).removeClass('open-menu').attr('ariahidden', 'true');
            $('.show-on-hover', obj).slideUp().attr('aria-hidden', 'true');
            $('.custom-read-more', obj).attr('tabindex', -1);
        }else{
            $(obj).addClass('open-menu');
            $('.show-on-hover', obj).stop(true, true).slideDown().attr('aria-hidden', 'false');
            $('.custom-read-more', obj).attr('tabindex', 0);
        }
    }

