	function pgScreenSize() {
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			//calendarListView(); commented out if statement by Lyndsay - Case 02848768 - 7.17.17
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			//calendarListView(); commented out if statement by Lyndsay - Case 02848768 - 7.17.17
			appView();
		}
	}

	function photoGallery() {
      if(!$("#hp-slideshow .rs-gallery-container").length){
        // MULTIMEDIA ROTATOR
        $("#hp-slideshow div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
                "imageWidth"              : 960,
                "imageHeight"             : 450,
                "efficientLoad"           : "no",
                "appHeader"               : "no",
                "titles"                  : "yes",
                "captions"                : "yes",
                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
                "playPause" 			  : "yes", // added by Matt - case#4093326 - 02.20.19
                "descMoreLink"            : "yes",
                "descMoreLinkText"        : "Learn More",
                "bullets"                 : "yes",
                "autoRotate"              : "yes",
                "transitionSpeed"         : 3,
                "transitionDelay"         : 8
        });
      }
	}

	function appView() {
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$(".hp.column.one div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-hp div.ui-widget-header").click(function(){
			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");
		});
	}	

	function useChannelForSection() {
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent() {
		// ********** MYSTART ********** //
		// DOC move #sw-mystart-inner to #gb-header-mystart
		$("#sw-mystart-inner").prependTo($("#gb-header-mystart"));
		// DOC move .sw-mystart-button.home to before .sw-mystart-dropdown.schoollist
		$(".sw-mystart-button.home").before($(".sw-mystart-dropdown.schoollist"));
		//DOC change .sw-mystart-dropdown.schoollist .selector text to Our Schools...
		$(".sw-mystart-dropdown.schoollist .selector").text("Our Schools...");

		// ********** HEADER ********** //
		//DOC add an id of #swsitesearch-btn-submit to the button input of a search active block
        	$("#gb-search input").eq(1).attr("id", "swsitesearch-btn-submit").show();
        	//DOC
        	if("shakopee" == "shakopee") {
        		$("#gb-logo").addClass("district-logo");
        		$('#hp-slideshow div.gallery-desc').hide();
        	}

		// ********** CHANNEL BAR ********** //
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC 
		$("ul.ui-breadcrumbs > li:not(.ui-breadcrumb-last) > a").append("  ");
		//DOC replace more-link text
		$("a.more-link span").text("Read More ");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        	}
        	//MOD EVENTS
		$("#hp-content .upcomingevents").modEvents({
			columns:"yes",
			monthLong:"no"
		});
		eventsByDay(".ui-hp .upcomingevents .ui-articles");
		if($(".caps a").attr("target") == "_self;_blank") {
			$(".caps a").attr("target", "_self");
		}

		// ********** FOOTER ********** //	
        $("#sw-footer-links ul li:first-of-type a").attr("href", "mailto:parenthelp@shakopee.k12.mn.us"); //added by Lyndsay - Case 00487531 - 2.15.17
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("#gb-search #swsitesearch-txt-searchstring").removeAttr("placeholder");
		$("#gb-search #swsitesearch-txt-searchstring").attr("value", "Search...");
		$("#gb-search #swsitesearch-txt-searchstring").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("#gb-search #swsitesearch-txt-searchstring").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	/*function calendarListView() { //COMMENTED OUT BY LYNDSAY AND ADDED UPDATED CALENDARLISTVIEW FUNCTION BELOW - CASE 02848768 - 7.17.17
