$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=OrgliDMNbKWYS%2fOA4vhuJQ%3d%3d&SessionID=dddfd3b2-e779-4a79-8445-9f545d94dc2b&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+8%3a57%3a36+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.27.245';
