    function MyStartDropdownActions(params) {
        var KeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

        var dropdownParent = params.dropdownParent;
        var dropdownSelector = params.dropdownSelector;
        var dropdown = params.dropdown;
        var dropdownList = params.dropdownList;

        // MYSTART DROPDOWN SELECTOR CLICK EVENT
        /*$(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});*/

        // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
        $(document).on("keydown", dropdownParent + " " + dropdownSelector, function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case KeyCodes.enter:
                case KeyCodes.space:
                    e.preventDefault();

                    // IF THE DROPDOWN IS OPEN, CLOSE IT
                    if($(dropdownParent).hasClass("open")){
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                    } else {
                        $(this).attr({"aria-expanded": "true"}).parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                            $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                        });
                    }

                break;

                // CONSUME TAB KEY
                case KeyCodes.tab:
                	if($(dropdownParent).hasClass("branded")) {

                    } else {
                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case KeyCodes.down:
                case KeyCodes.right:
                    e.preventDefault();

                    $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                    $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();

                break;
            }
        });

        // MYSTART DROPDOWN LINK KEYDOWN EVENTS
        $(document).on("keydown", dropdownParent + " " + dropdownList + " li a", function(e) {
            // CAPTURE KEY CODE
            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case KeyCodes.left:
                case KeyCodes.up:
                    e.preventDefault();

                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")) {
                        // FOCUS DROPDOWN BUTTON
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownParent).find(dropdownSelector).focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case KeyCodes.right:
                case KeyCodes.down:
                    e.preventDefault();

                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    } else {
                        // FOCUS NEXT ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                    }
                break;

                // CONSUME TAB KEY
                case KeyCodes.tab:
                    e.preventDefault();

                    if(e.shiftKey) {
                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    } else {
                        // IS LAST ITEM
                        /*if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }*/

                        //$(dropdownParent).next().find(dropdownSelector).focus();
                    }
                break;

                // CONSUME HOME KEY
                case KeyCodes.home:
                    e.preventDefault();

                    // FOCUS FIRST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                break;

                // CONSUME END KEY
                case KeyCodes.end:
                    e.preventDefault();

                    // FOCUS LAST ITEM
                    $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                    $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                break;
            }
        });

        $(".sw-mystart-dropdown.schoollist .sw-dropdown li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                case csKeyCodes.esc:
                    e.preventDefault();
                    $(".sw-mystart-dropdown.schoollist .sw-dropdown").slideUp().attr("aria-hidden","true"); 
                    if(!$(".sw-mystart-dropdown.translate").length) {
                    	$(".sw-channel-item").first().find("> a").focus();
                    } else {
                    	$(".sw-mystart-dropdown.translate .selector").focus();
                    }
                break;
            }
        });

        $(".sw-mystart-dropdown.translate .sw-dropdown li a").keydown(function(e) {
            switch(e.keyCode){
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.tab:
                case csKeyCodes.esc:
                    e.preventDefault();
                    $(".sw-mystart-dropdown.translate .sw-dropdown").slideUp().attr("aria-hidden","true"); 
                    $(".sw-channel-item").first().find("> a").focus();
                break;
            }
        });

        $(dropdownParent).mouseleave(function() {
        	var _this = this;

        	setTimeout(function () {
            	if(!$(_this).find(":hover").length) {
                  $("+ " + dropdownList + " a", this).attr("tabindex", "-1");
                  $(dropdownSelector, _this).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
              	}
            }, 500);
        }).focusout(function() {
        	var thisDropdown = this;
            setTimeout(function () {
                if(!$(thisDropdown).find(":focus").length) {
                    $(dropdownSelector, thisDropdown).attr({"aria-expanded": "false"}).parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                }
            }, 500);
        });
    }

