        "Translator": function() {
        	$("#sticky-footer").creativeTranslate({
                    "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
					"languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"]
					],
                "advancedOptions": {
                    "addMethod": "append",
                    "customElement": { // ONLY FOR FRAMESET AND API VERSIONS
                        "useCustomElement": true, 
                        "translateItemsList": true, // true = THE TRANSLATE ITEMS WILL BE AN UNORDERED LIST, false = THE TRANSLATE ITEMS WILL JUST BE A COLLECTION OF  TAGS
                        "customElementMarkup": "[$CreativeTranslateListItems$]" // CUSTOM HTML MARKUP THAT MAKES THE CUSTOM TRANSLATE ELEMENT/STRUCTURE - USE [$CreativeTranslateListItems$] ACTIVE BLOCK IN THE MARKUP WHERE THE TRANSLATE ITEMS SHOULD BE ADDED
                    },
                    "useTranslatedNames": true, // ONLY FOR FRAMESET AND API VERSIONS - true = TRANSLATED LANGUAGE NAME, false = ENGLISH LANGUAGE NAME
                },
                "translateLoaded": function() { } // CALLBACK TO RUN YOUR CUSTOM CODE AFTER EVERYTHING IS READY
            });

        	$("#espanol-link").click(function(e) {
                e.preventDefault();

                // SET LANGUAGE PAIR COOKIE
                csGlobalJs.CreateCookieNoEncoding({name:"googtrans", value: '/en/es'});

                // RELOAD THE PAGE
                location.reload();
            }).keydown(function(e) {
                if(e.keyCode == _this.KeyCodes.enter) {
                    e.preventDefault();

                    // SET LANGUAGE PAIR COOKIE
                    csGlobalJs.CreateCookieNoEncoding({name:"googtrans", value: '/en/es'});

                    // RELOAD THE PAGE
                    location.reload();
                }
            });

        },
