');
                $(".ui-article", this).each(function(i) {
                    if($(".ui-article-description", this).length > 2) {
                        $(".upcoming-column.right", this).append(' More');
                    }

                    $("p:nth-of-type(n+3)", this).wrapAll('
');
                });
            });

            $(document).on("click keydown", ".cs-more-events-btn", function(e) {
                if(CreativeTemplate.AllyClick(e)) {
                    e.preventDefault();

                    var dropdownId = $(this).attr("data-dropdown-id");

                    if($(this).attr("aria-expanded") == "false") {
                        $(this).attr("aria-expanded", "true");
                        $("#" + dropdownId).slideDown();
                    } else {
                        $(this).attr("aria-expanded", "false");
                        $("#" + dropdownId).slideUp();
                    }
                }
            });
        },
