								$("#hp-slideshow .rs-gallery-container .rs-gallery-images ul.rs-gallery-images-list").append(hpScroll);
								var hpTagline = '

Educate. Inspire. Empower.';
								$("#hp-slideshow .rs-gallery-container .rs-gallery-images ul.rs-gallery-images-list").append(hpTagline);
		                					$("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").html("l");
								$("#hp-slideshow .rs-gallery-container .rs-gallery-control.next").html("r");
		                					var getWidth = $(window).width();
								var contentWidth = $("#gb-header").width();
								var galleryDescOffset = ((getWidth-contentWidth)/2) + 35;
								$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("padding-left",galleryDescOffset);
								var controlBackHor = (getWidth-contentWidth)/2;
								$("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").css("left",controlBackHor);
		                					$(window).resize(function(){
									var getWidth = $(window).width();
									var contentWidth = $("#gb-header").width();
									var galleryDescOffset = ((getWidth-contentWidth)/2) + 35;
									$("#hp-slideshow .rs-gallery-container .rs-gallery-desc").css("padding-left",galleryDescOffset);
									var controlBackHor = (getWidth-contentWidth)/2;
									$("#hp-slideshow .rs-gallery-container .rs-gallery-control.back").css("left",controlBackHor);
								});
							}	
		        });
		}
	}

