	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-headlines div.ui-widget.app:first").addClass("active");
		} 

		$("div.ui-hp div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
			if(!$("#hp-headlines div.ui-widget.app.active").length){
				$("#headlines-arrow-left").addClass("hidden");
				$("#headlines-arrow-right").addClass("hidden");
			} else {
				$("#headlines-arrow-left").removeClass("hidden");
				$("#headlines-arrow-right").removeClass("hidden");
			}
		});
	}	

