	var topScroll = function(){
		// BACK TO TOP BUTTON
		var pageH = $("#gb-page").height() / 3;

		// SHOW OR HIDE BACK TO TOP BUTTON
		if($(window).scrollTop() < pageH) {
			$(".desktop a#back-to-top").fadeOut();
		} else {
			$(".desktop a#back-to-top").fadeIn();
		}

	}

    function stickyCalendarFix() {
    	var headerHeight = $("#gb-header").height();

        if ($(window).scrollTop() <= headerHeight) {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                $(".wcm-controls").css("margin-top", "0px");
            }
        } else {

            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                if($(".wcm-controls").hasClass("wcm-stuck")) {
                    $(".wcm-controls").css("margin-top", headerHeight + "px");
                }
            }
        }
    }

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	//REMOVE LOGO ERROR IF NONE SELECTED
	function removeLogo(){
		$("#gb-logo").remove();
		$("#gb-sitename").css({
				"width": "auto",
				"padding-left": "0"
		});
		$("#gb-tagline").css("padding-left","0")
	}

	function eventsByDay(container) {
		//DOC move all events with the same dates into the same container
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			}
			if(parseInt($(".joel-date", this).text()) < 10) {
				$(".joel-date", this).prepend("0");
			}
		});

		if($(".hp").length) {

			// ADD CLASSES TO ARTICLES
			$("#hp-content-bottom .upcomingevents .ui-articles").wrap("
");
			$("#hp-content-bottom .upcomingevents li").first().addClass("first");
			$("#hp-content-bottom .upcomingevents li").last().addClass("last");
			var eventClass = ["one", "two", "three", "four", "five"];
			$("#hp-content-bottom .upcomingevents li").each(function() {
				var thisIndex = $(this).index();
				$(this).addClass("event-" + eventClass[thisIndex]);
			});
		}

	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	var parallaxScroll = function(){
		if($(".hp").length){
			var getWidth = $(window).width();
			if (getWidth >= 1024) { // 960+
				if($(window).scrollTop() >= 200) {
					//$("#gb-header-sticky").removeClass("hidden");
					$("#gb-header").addClass("sticky");
				} else {
					//$("#gb-header-sticky").addClass("hidden");
					$("#gb-header").removeClass("sticky");
				}
			} if(getWidth >= 768 && getWidth < 1024) {
				if($(window).scrollTop() >= 161) {
					//$("#gb-header-sticky").removeClass("hidden");
					$("#gb-header").addClass("sticky");
				} else {
					//$("#gb-header-sticky").addClass("hidden");
					$("#gb-header").removeClass("sticky");
				}
			}

		}
		if($(".sp").length){
			//var getWidth = $(window).width();
			//if (getWidth >= 768) { // 960+
				if($(window).scrollTop() >= 42) {
					$(".sp #gb-header").addClass("fixed");
					$("#sp-content-outer").css("padding-top","79px");
					$("#spn-content-outer").css("padding-top","79px");
				} else {
					$(".sp #gb-header").removeClass("fixed");
					$("#sp-content-outer").css("padding-top","0px");
					$("#spn-content-outer").css("padding-top","0px");
				}
			//}	
		}

	}

	function globalIcons(){
		        var directoryTarget = 'Same Window';

			if(directoryTarget == "New Window"){
		        		directoryTarget = "_blank";
		        } else {
			        	directoryTarget = "_self";
			}
		        $(".icn.five").attr("target", directoryTarget);
		}

	function socialIcons(){
		// SET UP THE CHECK VARIABLES
		var hasIcons = 0;
		var facebookCheck = 'true';
		var instagramCheck = 'false';
		var linkedInCheck = 'false';
		var vimeoCheck = 'false';
		var pinterestCheck = 'false';
		var twitterCheck = 'true';

		// SHOW THE ICONS THAT ARE NEEDED
	    	if(facebookCheck == "true"){
		        var facebookTarget = 'New Window';
		        if(facebookTarget == "New Window"){
		        	facebookTarget = "_blank";
		        } else {
		        	facebookTarget = "_self";
		        }
		        $(".icn.facebook").removeClass("hidden").attr("target", facebookTarget);
			hasIcons = 1;
		}
	    	if(instagramCheck == "true"){
		        var instagramTarget = 'Same Window';
		        if(instagramTarget == "New Window"){
		        	instagramTarget = "_blank";
		        } else {
		        	instagramTarget = "_self";
		        }
		        $(".icn.instagram").removeClass("hidden").attr("target", instagramTarget);
		        hasIcons = 1;
		}
		if(linkedInCheck == "true"){
		        var linkedInTarget = 'Same Window';
		        if(linkedInTarget == "New Window"){
		        	linkedInTarget = "_blank";
		        } else {
		        	linkedInTarget = "_self";
		        }
		        $(".icn.linkedIn").removeClass("hidden").attr("target", linkedInTarget);
		        hasIcons = 1;
		}
		if(vimeoCheck == "true"){
		        var vimeoTarget = 'Same Window';
		        if(vimeoTarget == "New Window"){
		        	vimeoTarget = "_blank";
		        } else {
		        	vimeoTarget = "_self";
		        }
		        $(".icn.vimeo").removeClass("hidden").attr("target", vimeoTarget);
		        hasIcons = 1;
		}
		if(pinterestCheck == "true"){
		        var pinterestTarget = 'Same Window';
		        if(pinterestTarget == "New Window"){
		        	pinterestTarget = "_blank";
		        } else {
		        	pinterestTarget = "_self";
		        }
		        $(".icn.pinterest").removeClass("hidden").attr("target", pinterestTarget);
		        hasIcons = 1;
		}
		if(twitterCheck == "true"){
		        var twitterTarget = 'New Window';
		        if(twitterTarget == "New Window"){
		        	twitterTarget = "_blank";
		        } else {
		        	twitterTarget = "_self";
		        }
		        $(".icn.twitter").removeClass("hidden").attr("target", twitterTarget);
		        hasIcons = 1;
		}

		// SHOW THE ICONS IF THERE ARE ANY
		if(hasIcons == 1){
			$("#gb-social-icons").removeClass("hidden");
			$(".gb.footer.three h2").removeClass("hidden");
		}
	}

	function scroller() {
			$("#hp-headlines .ui-widget.app.headlines li").last().addClass("last");
			$("#hp-headlines .ui-articles").css("margin-left","0");
			var headlineWidth = 0;
			$("#hp-headlines .ui-widget.app.headlines .ui-widget-detail .ui-articles li").each(function() {
				headlineWidth += $(this).outerWidth(true);
			});
			$("#hp-headlines .ui-articles").css("width", headlineWidth);

			if($("#hp-headlines .ui-articles").width() > $("#hp-headlines").width()){
				$("#headlines-arrow-right").removeClass("hidden");
				$("#headlines-arrow-left").removeClass("hidden");
			}

			// SCROLL FUNCTION
			$(".headlines-arrow").click(function() {
				var scrollDistance = $("#hp-headlines .ui-widget.app.headlines .ui-widget-detail .ui-articles li").outerWidth(true);
				var firstScrollDistance = $("#hp-headlines .ui-widget.app.headlines .ui-widget-detail .ui-articles li").first().outerWidth(true);
				var lastScrollDistance = $("#hp-headlines .ui-widget.app.headlines .ui-widget-detail .ui-articles li").last().outerWidth(true);
				var slidePos = parseInt($("#hp-headlines .ui-articles").css("margin-left")) * -1;
				var overflowDistance = parseInt($("#hp-headlines .ui-articles").css("width")) - $("#hp-headlines .ui-widget.app.headlines .ui-widget-detail").width();
				var lastScroll = overflowDistance - slidePos;
				var newPos = 0;

				// ANIMATE THE THUMBNAIL SLIDE
				if($(this).hasClass("next")) {
				    if(lastScroll < lastScrollDistance) {
				        newPos = slidePos + lastScroll;
				    } else {
				        newPos = slidePos + scrollDistance;
				    }    

				    $("#hp-headlines .ui-articles").animate({
				        marginLeft: "-" + newPos + "px"
				    }, 400);
				} else if($(this).hasClass("back")) {
				    if(slidePos < firstScrollDistance) {
				        newPos = 0;
				    } else {
				        newPos = slidePos - scrollDistance;   
				    }

				    $("#hp-headlines .ui-articles").animate({
				        marginLeft: "-" + newPos + "px"
				    }, 400);
				}	
			});

	}

	function scrollerResize() {
			$(".hp-column-top .ui-articles").css("margin-left","0");
			var headlineWidth = 0;
			$(".hp-column-top .ui-widget.app.headlines .ui-widget-detail .ui-articles li").each(function() {
				headlineWidth += $(this).outerWidth(true);
			});
			$(".hp-column-top .ui-articles").css("width", headlineWidth);

			if($(".hp-column-top .ui-articles").width() > $(".hp-column-top").width()){
				$("#headlines-arrow-right").removeClass("hidden");
				$("#headlines-arrow-left").removeClass("hidden");
			}
	}

	function buildStyleSheet(){
		var secondaryColor80 = "rgba(" + hexToRgb('#f26b21') + ", .80)";
		var primaryColor = "#004a80";
		primaryColor = primaryColor.replace('#', '');

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-slideshow .rs-gallery-container .rs-gallery-desc {' +
                    				'background: ' + secondaryColor80 + ';' +
						'-pie-background: ' + secondaryColor80 + ';' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

