div.script:
	function ClientNodeClicked(sender, eventArgs) {
				
		
		var node = eventArgs.get_node();
		
		
		//node.toggle();
		
		if (node.get_expanded()==true) {
			node.set_selected(false);
			node.collapse();
		}
		else{
			node.set_selected(true);
			node.expand();
		}
		
		CollapseSiblings(node);
	}
	
	function treeCollapseAllNodes() {
		var treeView = $find("ctl00_ctl00_TelerikMenuTree1_RadTreeView1");
		var nodes = treeView.get_allNodes();
		for (var i = 0; i < nodes.length; i++) {
			if (nodes[i].get_nodes() != null) {
				nodes[i].collapse();
			}
		}
	}
	
	function CollapseSiblings(node) {
		var parent = node.get_parent();
		var siblings = parent.get_nodes();
		var siblingsCount = siblings.get_count();

		for (var nodeIndex = 0; nodeIndex < siblingsCount; nodeIndex++)
		 {
			var siblingNode = siblings.getNode(nodeIndex);

			if ((siblingNode != node) && (siblingNode.get_expanded())) 
			{
				siblingNode.collapse();
				return;
			}
		}
	}

	function ClientNodeClicked(sender, eventArgs) {
				
		
		var node = eventArgs.get_node();
		
		
		//node.toggle();
		
		if (node.get_expanded()==true) {
			node.set_selected(false);
			node.collapse();
		}
		else{
			node.set_selected(true);
			node.expand();
		}
		
		CollapseSiblings(node);
	}
	
	function treeCollapseAllNodes() {
		var treeView = $find("ctl00_ctl00_TelerikMenuTree2_RadTreeView1");
		var nodes = treeView.get_allNodes();
		for (var i = 0; i < nodes.length; i++) {
			if (nodes[i].get_nodes() != null) {
				nodes[i].collapse();
			}
		}
	}
	
	function CollapseSiblings(node) {
		var parent = node.get_parent();
		var siblings = parent.get_nodes();
		var siblingsCount = siblings.get_count();

		for (var nodeIndex = 0; nodeIndex < siblingsCount; nodeIndex++)
		 {
			var siblingNode = siblings.getNode(nodeIndex);

			if ((siblingNode != node) && (siblingNode.get_expanded())) 
			{
				siblingNode.collapse();
				return;
			}
		}
	}

